    const cacheKey = getCachedAudioKey(text, voiceKey);
    const cacheData = {
      audioUrl,
      duration,
      timestamp: Date.now(),
      text: text.substring(0, 100) // Store first 100 chars for reference
    };
    localStorage.setItem(cacheKey, JSON.stringify(cacheData));
