        setLocalIsGeneratingDebug(false);
        setIsStartingPlayback(false);
        isInitializingPlayback.current = false;
        console.log('🔧 Set isInitializingPlayback to false (AI TTS error)');
        setIsUsingAITTS(false); // Reset flag on failure
      }

