server/routes/data-upload-routes.ts.content:
import { Router } from 'express';
import { dataUploadService, UploadedTestData, DataUploadOptions } from '../services/data-upload-service';
import { authenticateToken, requireAdmin, type AuthenticatedRequest } from '../middleware/auth';
import { storage } from '../storage';

const router = Router();

// Upload practice test data
router.post('/upload-practice-test', authenticateToken, requireAdmin, async (req: AuthenticatedRequest, res) => {
  try {
    const testData: UploadedTestData = req.body;
    const options: DataUploadOptions = req.body.options || {};

    const result = await dataUploadService.uploadPracticeTestData(testData, options);

    if (result.success) {
      // Log the action
      await storage.createAuditLog({
        adminId: req.user!.userId,
        action: 'upload_practice_test',
        entityId: result.testId!,
        details: { 
          title: testData.title, 
          category: testData.category,
          questionCount: testData.questions.length 
        },
        timestamp: new Date()
      });
    }

    res.json(result);
  } catch (error: any) {
    console.error('Error uploading practice test:', error);
    res.status(500).json({ 
      success: false, 
      message: 'Failed to upload practice test',
      error: error.message 
    });
  }
});

// Upload batch tests
router.post('/upload-batch-tests', authenticateToken, requireAdmin, async (req: AuthenticatedRequest, res) => {
  try {
    const { tests, options } = req.body;

    if (!tests || !Array.isArray(tests)) {
      return res.status(400).json({ 
        success: false, 
        message: 'Tests array is required' 
      });
    }

    const result = await dataUploadService.uploadBatchTests(tests, options);

    // Log the action
    await storage.createAuditLog({
      adminId: req.user!.userId,
      action: 'upload_batch_tests',
      entityId: 'multiple',
      details: { 
        testCount: tests.length,
        successfulUploads: result.summary.successfulUploads,
        failedUploads: result.summary.failedUploads
      },
      timestamp: new Date()
    });

    res.json(result);
  } catch (error: any) {
    console.error('Error uploading batch tests:', error);
    res.status(500).json({ 
      success: false, 
      message: 'Failed to upload batch tests',
      error: error.message 
    });
  }
});

// Upload the given question pool data
router.post('/upload-question-pools', authenticateToken, requireAdmin, async (req: AuthenticatedRequest, res) => {
  try {
    const { questionPools } = req.body;

    if (!questionPools || typeof questionPools !== 'object') {
      return res.status(400).json({ 
        success: false, 
        message: 'Question pools data is required' 
      });
    }

    const result = await dataUploadService.createComprehensiveTestsFromData(questionPools);

    // Log the action
    await storage.createAuditLog({
      adminId: req.user!.userId,
      action: 'upload_question_pools',
      entityId: 'multiple',
      details: { 
        categories: Object.keys(questionPools),
        createdTests: result.createdTests.length
      },
      timestamp: new Date()
    });

    res.json(result);
  } catch (error: any) {
    console.error('Error uploading question pools:', error);
    res.status(500).json({ 
      success: false, 
      message: 'Failed to upload question pools',
      error: error.message 
    });
  }
});

// Get upload status and analytics
router.get('/upload-status/:testId', authenticateToken, requireAdmin, async (req: AuthenticatedRequest, res) => {
  try {
    const { testId } = req.params;
    const { testType = 'practice' } = req.query;

    const analytics = await dataUploadService['enhancedTestManager'].getEnhancedTestAnalytics(
      testId, 
      testType as 'practice' | 'mock'
    );

    res.json({
      success: true,
      testId,
      analytics
    });
  } catch (error: any) {
    console.error('Error getting upload status:', error);
    res.status(500).json({ 
      success: false, 
      message: 'Failed to get upload status',
      error: error.message 
    });
  }
});

// Validate test data before upload
router.post('/validate-test-data', authenticateToken, requireAdmin, async (req: AuthenticatedRequest, res) => {
  try {
    const testData: UploadedTestData = req.body;

    // Validate the data structure
    try {
      dataUploadService['validateTestData'](testData);
    } catch (error: any) {
      return res.json({
        valid: false,
        errors: [error.message],
        warnings: []
      });
    }

    // Validate questions
    const questionValidation = await dataUploadService['validateQuestions'](testData.questions);

    res.json({
      valid: questionValidation.valid,
      errors: questionValidation.errors,
      warnings: questionValidation.warnings,
      questionCount: testData.questions.length,
      estimatedDifficulty: dataUploadService['calculateDifficulty'](testData.questions)
    });
  } catch (error: any) {
    console.error('Error validating test data:', error);
    res.status(500).json({ 
      success: false, 
      message: 'Failed to validate test data',
      error: error.message 
    });
  }
});

// Get upload history
router.get('/upload-history', authenticateToken, requireAdmin, async (req: AuthenticatedRequest, res) => {
  try {
    const { page = '1', limit = '20' } = req.query;
    const pageNum = parseInt(page as string);
    const limitNum = parseInt(limit as string);

    const auditLogs = await storage.getAuditLogs({
      page: pageNum,
      limit: limitNum
    });

    const uploadLogs = auditLogs.filter(log => 
      log.action.includes('upload') || log.action.includes('create')
    );

    res.json({
      success: true,
      uploads: uploadLogs,
      pagination: {
        page: pageNum,
        limit: limitNum,
        total: uploadLogs.length
      }
    });
  } catch (error: any) {
    console.error('Error getting upload history:', error);
    res.status(500).json({ 
      success: false, 
      message: 'Failed to get upload history',
      error: error.message 
    });
  }
});

export default router;

shared/schema.ts.content:
import { sql } from "drizzle-orm";
import { pgTable, text, varchar, integer, boolean, jsonb, timestamp } from "drizzle-orm/pg-core";
import { createInsertSchema } from "drizzle-zod";
import { z } from "zod";

export const users = pgTable("users", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  firstName: text("first_name").notNull(),
  username: text("username").notNull().unique(),
  password: text("password").notNull(),
  email: text("email").notNull().unique(),
  role: text("role").notNull().default('student'), // 'student' | 'admin' | 'superadmin'
  overallProgress: integer("overall_progress").notNull().default(0),
  totalStudyTime: integer("total_study_time").notNull().default(0), // in minutes
  currentStreak: integer("current_streak").notNull().default(0),
  stripeCustomerId: text("stripe_customer_id"),
  stripeSubscriptionId: text("stripe_subscription_id"),
  subscriptionType: text("subscription_type"), // 'basic', 'group', 'guidance'
  subscriptionStatus: text("subscription_status"), // 'active', 'canceled', 'past_due'
  isSuspended: boolean("is_suspended").notNull().default(false),
  isDeleted: boolean("is_deleted").notNull().default(false),
  suspensionReason: text("suspension_reason"),
  deletedAt: timestamp("deleted_at"),
  createdAt: timestamp("created_at").defaultNow(),
});

export const videoModules = pgTable("video_modules", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  title: text("title").notNull(),
  description: text("description").notNull(),
  videoUrl: text("video_url").notNull(),
  duration: integer("duration").notNull(), // in seconds
  thumbnail: text("thumbnail"),
  category: text("category").notNull(),
  orderIndex: integer("order_index").notNull(),
  detailedContent: text("detailed_content"), // Rich text content about the topic
  keyImages: jsonb("key_images"), // Array of relevant images with descriptions
  audioScript: text("audio_script"), // Script for AI voice generation
  // Enhanced content management fields
  videoType: text("video_type").notNull().default('uploaded'), // 'uploaded' | 'youtube'
  isActive: boolean("is_active").notNull().default(true),
  difficulty: text("difficulty").default('intermediate'), // 'beginner' | 'intermediate' | 'advanced'
  tags: jsonb("tags"), // Array of tags
  additionalContent: text("additional_content"), // Additional educational content
  instructorNotes: text("instructor_notes"), // Teaching notes
  studentNotes: text("student_notes"), // Study notes for students
  keyPoints: text("key_points"), // Key points to remember
  prerequisites: text("prerequisites"), // What students should know before
  followUpActions: text("follow_up_actions"), // What to do after watching
  createdAt: timestamp("created_at").defaultNow(),
  updatedAt: timestamp("updated_at").defaultNow(),
});

export const userVideoProgress = pgTable("user_video_progress", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  userId: varchar("user_id").references(() => users.id).notNull(),
  videoId: varchar("video_id").references(() => videoModules.id).notNull(),
  completed: boolean("completed").notNull().default(false),
  watchTime: integer("watch_time").notNull().default(0), // in seconds
  lastWatched: timestamp("last_watched").defaultNow(),
});

export const timelineEvents = pgTable("timeline_events", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  year: integer("year").notNull(),
  title: text("title").notNull(),
  description: text("description").notNull(),
  details: text("details"),
  category: text("category").notNull(), // parliament, documents, voting_rights, territories, trades
  importance: integer("importance").notNull().default(1), // 1-5 scale
  keyFigures: text("key_figures"), // Notable personalities involved
  timelineTopic: text("timeline_topic").notNull(), // Which timeline this belongs to
  eventImage: text("event_image"), // URL to representative image
  imageDescription: text("image_description"), // Description of the image
});

export const exercises = pgTable("exercises", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  userId: varchar("user_id").references(() => users.id).notNull(),
  content: jsonb("content").notNull(), // AI-generated exercise content
  topic: text("topic").notNull(),
  difficulty: integer("difficulty").notNull().default(1), // 1-5 scale
  createdAt: timestamp("created_at").defaultNow(),
});

export const exerciseAttempts = pgTable("exercise_attempts", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  userId: varchar("user_id").references(() => users.id).notNull(),
  exerciseId: varchar("exercise_id").references(() => exercises.id).notNull(),
  answers: jsonb("answers").notNull(),
  score: integer("score").notNull(),
  totalQuestions: integer("total_questions").notNull(),
  completedAt: timestamp("completed_at").defaultNow(),
});

export const learningModules = pgTable("learning_modules", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  title: text("title").notNull(),
  description: text("description").notNull(),
  orderIndex: integer("order_index").notNull(),
  totalItems: integer("total_items").notNull(),
});

export const userModuleProgress = pgTable("user_module_progress", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  userId: varchar("user_id").references(() => users.id).notNull(),
  moduleId: varchar("module_id").references(() => learningModules.id).notNull(),
  completedItems: integer("completed_items").notNull().default(0),
  progress: integer("progress").notNull().default(0), // percentage
});

export const resources = pgTable("resources", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  title: text("title").notNull(),
  description: text("description"),
  fileUrl: text("file_url").notNull(),
  fileSize: text("file_size").notNull(),
  fileType: text("file_type").notNull(),
  category: text("category").notNull(),
});

export const practiceTests = pgTable("practice_tests", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  title: text("title").notNull(),
  description: text("description").notNull(),
  category: text("category").notNull(),
  difficulty: integer("difficulty").notNull().default(1), // 1-5 scale
  questions: jsonb("questions").notNull(), // Array of 24 questions with 4 options each
  orderIndex: integer("order_index").notNull(),
});

export const practiceTestAttempts = pgTable("practice_test_attempts", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  userId: varchar("user_id").references(() => users.id).notNull(),
  testId: varchar("test_id").references(() => practiceTests.id).notNull(),
  answers: jsonb("answers").notNull(), // User's selected answers
  score: integer("score").notNull(),
  totalQuestions: integer("total_questions").notNull().default(24),
  passedTest: boolean("passed_test").notNull(), // true if score >= 18/24 (75%)
  completedAt: timestamp("completed_at").defaultNow(),
});

export const mockTests = pgTable("mock_tests", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  title: text("title").notNull(),
  description: text("description").notNull(),
  questions: jsonb("questions").notNull(), // Array of exactly 24 questions with 4 options each
  orderIndex: integer("order_index").notNull(),
  difficulty: integer("difficulty").notNull().default(3), // Mock tests are typically medium difficulty
  createdAt: timestamp("created_at").defaultNow(),
});

export const mockTestAttempts = pgTable("mock_test_attempts", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  userId: varchar("user_id").references(() => users.id).notNull(),
  mockTestId: varchar("mock_test_id").references(() => mockTests.id).notNull(),
  answers: jsonb("answers").notNull(), // User's selected answers (array of 24 numbers)
  score: integer("score").notNull(),
  totalQuestions: integer("total_questions").notNull().default(24),
  passedTest: boolean("passed_test").notNull(), // true if score >= 18/24 (75%)
  timeSpent: integer("time_spent").notNull(), // in seconds (max 2700 = 45 minutes)
  completedAt: timestamp("completed_at").defaultNow(),
});

export const refreshTokens = pgTable("refresh_tokens", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  token: text("token").notNull().unique(),
  userId: varchar("user_id").references(() => users.id).notNull(),
  expiresAt: timestamp("expires_at").notNull(),
  createdAt: timestamp("created_at").defaultNow(),
});

export const adminAuditLogs = pgTable("admin_audit_logs", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  adminUserId: varchar("admin_user_id").references(() => users.id).notNull(),
  action: text("action").notNull(), // 'create', 'update', 'delete'
  entity: text("entity").notNull(), // 'user', 'practiceTest', 'mockTest', etc.
  entityId: text("entity_id").notNull(),
  before: jsonb("before"), // Previous state
  after: jsonb("after"), // New state
  ip: text("ip"),
  createdAt: timestamp("created_at").defaultNow(),
});

// Video Resources Table
export const videoResources = pgTable("video_resources", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  videoId: varchar("video_id").references(() => videoModules.id).notNull(),
  title: text("title").notNull(),
  type: text("type").notNull(), // 'pdf', 'link', 'file', 'image'
  url: text("url").notNull(),
  description: text("description"),
  size: text("size"), // File size for files
  uploadedAt: timestamp("uploaded_at").defaultNow(),
  createdAt: timestamp("created_at").defaultNow(),
});

// Video Audio Table
export const videoAudio = pgTable("video_audio", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  videoId: varchar("video_id").references(() => videoModules.id).notNull(),
  title: text("title").notNull(),
  type: text("type").notNull(), // 'narration', 'accessibility', 'background'
  url: text("url").notNull(),
  description: text("description"),
  duration: text("duration"), // Audio duration
  quality: text("quality"), // Audio quality
  uploadedAt: timestamp("uploaded_at").defaultNow(),
  createdAt: timestamp("created_at").defaultNow(),
});

// Insert schemas
export const insertUserSchema = createInsertSchema(users).omit({
  id: true,
  createdAt: true,
});

export const insertVideoModuleSchema = createInsertSchema(videoModules).omit({
  id: true,
});

export const insertUserVideoProgressSchema = createInsertSchema(userVideoProgress).omit({
  id: true,
  lastWatched: true,
});

