// Create 24 questions for a test
function createTestQuestions(testIndex) {
  const testQuestions = [];
  const questionsPerCategory = 6; // 6 questions from each of 4 categories

  const categories = ['British History', 'Government & Politics', 'Geography & Culture', 'Laws & Society'];

  for (const category of categories) {
    const categoryQuestions = sampleQuestions.filter(q => q.category === category);
    for (let i = 0; i < questionsPerCategory; i++) {
      const questionIndex = (testIndex + i) % categoryQuestions.length;
      const question = categoryQuestions[questionIndex];
      testQuestions.push({
        id: testQuestions.length + 1,
        question: question.question,
        options: question.options,
        correctAnswer: question.correctAnswer,
        explanation: question.explanation,
        category: question.category
      });
    }
  }

  return testQuestions;
}

async function updatePracticeTests() {
  const client = await pool.connect();

  try {
    console.log('🚀 Starting to update practice tests with questions...');

    // Get all existing practice tests
    const result = await client.query('SELECT id, title, order_index FROM practice_tests ORDER BY order_index');
    const existingTests = result.rows;

    console.log(`📋 Found ${existingTests.length} existing practice tests`);

    if (existingTests.length === 0) {
      console.log('❌ No practice tests found. Please create some practice tests first.');
      return;
    }

    // Update each test with questions
    for (const test of existingTests) {
      console.log(`📝 Updating test: ${test.title}`);

      // Create 24 questions for this test
      const testQuestions = createTestQuestions(test.order_index);

      // Update the test with questions
      await client.query(
        'UPDATE practice_tests SET questions = $1 WHERE id = $2',
        [JSON.stringify(testQuestions), test.id]
      );

      console.log(`✅ Updated ${test.title} with ${testQuestions.length} questions`);
    }

    console.log('\n🎉 Practice tests update complete!');
    console.log('📊 Summary:');
    console.log(`   - Tests updated: ${existingTests.length}`);
    console.log(`   - Questions per test: 24`);
    console.log(`   - Total questions added: ${existingTests.length * 24}`);

  } catch (error) {
    console.error('❌ Error updating practice tests:', error);
    console.error(error);
  } finally {
    client.release();
    await pool.end();
  }
}

// Run the update
updatePracticeTests().catch(console.error);

