interface UKMembershipsMatchingProps {
  userId: string;
}

export default function UKMembershipsMatching({ userId }: UKMembershipsMatchingProps) {
  const [organizations, setOrganizations] = useState<MembershipData[]>([]);
  const [roles, setRoles] = useState<MembershipData[]>([]);
  const [details, setDetails] = useState<MembershipData[]>([]);
  const [selectedOrganization, setSelectedOrganization] = useState<MembershipData | null>(null);
  const [selectedRole, setSelectedRole] = useState<MembershipData | null>(null);
  const [selectedDetail, setSelectedDetail] = useState<MembershipData | null>(null);
  const [matchedItems, setMatchedItems] = useState<Set<string>>(new Set());
  const [incorrectAttempts, setIncorrectAttempts] = useState<Set<string>>(new Set());
  const [score, setScore] = useState(0);
  const [attempts, setAttempts] = useState(0);
  const [filterCategory, setFilterCategory] = useState<string>('all');
  const [filterStatus, setFilterStatus] = useState<string>('all');
  const [gameComplete, setGameComplete] = useState(false);

  const categories = ['all', 'International', 'European', 'Commonwealth', 'Security', 'Economic', 'Cultural', 'Environmental'];
  const statuses = ['all', 'Current', 'Former', 'Founding'];

  const getFilteredData = () => {
    let filtered = ukMembershipsData;

    if (filterCategory !== 'all') {
      filtered = filtered.filter(item => item.category === filterCategory);
    }

    if (filterStatus !== 'all') {
      filtered = filtered.filter(item => item.status === filterStatus);
    }

    return filtered;
  };

  const shuffleArray = <T,>(array: T[]): T[] => {
    const shuffled = [...array];
    for (let i = shuffled.length - 1; i > 0; i--) {
      const j = Math.floor(Math.random() * (i + 1));
      [shuffled[i], shuffled[j]] = [shuffled[j], shuffled[i]];
    }
    return shuffled;
  };

  const initializeGame = () => {
    const filteredData = getFilteredData();
    setOrganizations(shuffleArray(filteredData));
    setRoles(shuffleArray(filteredData));
    setDetails(shuffleArray(filteredData));
    setMatchedItems(new Set());
    setIncorrectAttempts(new Set());
    setSelectedOrganization(null);
    setSelectedRole(null);
    setSelectedDetail(null);
    setScore(0);
    setAttempts(0);
    setGameComplete(false);
  };

  useEffect(() => {
    initializeGame();
  }, [filterCategory, filterStatus]);

  const handleOrganizationClick = (org: MembershipData) => {
    if (matchedItems.has(org.id)) return;
    setSelectedOrganization(org);
    setIncorrectAttempts(new Set());
  };

  const handleRoleClick = (role: MembershipData) => {
    if (matchedItems.has(role.id)) return;
    setSelectedRole(role);
    setIncorrectAttempts(new Set());
  };

  const handleDetailClick = (detail: MembershipData) => {
    if (matchedItems.has(detail.id)) return;
    setSelectedDetail(detail);
    setIncorrectAttempts(new Set());
  };

  useEffect(() => {
    if (selectedOrganization && selectedRole && selectedDetail) {
      setAttempts(prev => prev + 1);

      if (selectedOrganization.id === selectedRole.id && 
          selectedRole.id === selectedDetail.id) {
        // Correct match!
        celebrateWithTheme('international');
        setMatchedItems(prev => new Set(Array.from(prev).concat(selectedOrganization.id)));
        setScore(prev => prev + 1);

        // Check if game is complete
        const filteredData = getFilteredData();
        if (matchedItems.size + 1 >= filteredData.length) {
          setGameComplete(true);
          setTimeout(() => celebrateWithTheme('international'), 500);
        }
      } else {
        // Incorrect match
        const incorrectIds = new Set([selectedOrganization.id, selectedRole.id, selectedDetail.id]);
        setIncorrectAttempts(incorrectIds);

        setTimeout(() => {
          setIncorrectAttempts(new Set());
        }, 3000);
      }

      // Reset selections
      setTimeout(() => {
        setSelectedOrganization(null);
        setSelectedRole(null);
        setSelectedDetail(null);
      }, incorrectAttempts.size > 0 ? 3000 : 1000);
    }
  }, [selectedOrganization, selectedRole, selectedDetail, matchedItems.size]);

  const getButtonStyle = (item: MembershipData, isSelected: boolean) => {
    const baseClasses = "w-full text-left p-3 rounded-lg border transition-all duration-300 hover:shadow-md";
    const isMatched = matchedItems.has(item.id);
    const isIncorrect = incorrectAttempts.has(item.id);

    if (isMatched) {
      return `${baseClasses} bg-emerald-100 dark:bg-emerald-900/30 text-emerald-800 dark:text-emerald-200 border-emerald-300 dark:border-emerald-700 cursor-default shadow-sm`;
    }

    if (isIncorrect) {
      return `${baseClasses} bg-red-100 dark:bg-red-900/30 text-red-800 dark:text-red-200 border-red-300 dark:border-red-700 animate-pulse cursor-pointer`;
    }

    if (isSelected) {
      return `${baseClasses} bg-slate-100 dark:bg-slate-900/30 text-slate-800 dark:text-slate-200 border-slate-300 dark:border-slate-700 shadow-lg cursor-pointer`;
    }

    return `${baseClasses} bg-white dark:bg-gray-800 border-gray-200 dark:border-gray-700 text-gray-800 dark:text-white hover:bg-gray-50 dark:hover:bg-gray-700 cursor-pointer`;
  };

  const getCategoryColor = (category: string) => {
    const colors = {
      International: 'bg-blue-100 text-blue-800 dark:bg-blue-900/30 dark:text-blue-300',
      European: 'bg-violet-100 text-violet-800 dark:bg-violet-900/30 dark:text-violet-300',
      Commonwealth: 'bg-red-100 text-red-800 dark:bg-red-900/30 dark:text-red-300',
      Security: 'bg-orange-100 text-orange-800 dark:bg-orange-900/30 dark:text-orange-300',
      Economic: 'bg-emerald-100 text-emerald-800 dark:bg-emerald-900/30 dark:text-emerald-300',
      Cultural: 'bg-pink-100 text-pink-800 dark:bg-pink-900/30 dark:text-pink-300',
      Environmental: 'bg-teal-100 text-teal-800 dark:bg-teal-900/30 dark:text-teal-300'
    };
    return colors[category as keyof typeof colors] || 'bg-gray-100 text-gray-800 dark:bg-gray-700 dark:text-gray-200';
  };

  const getStatusColor = (status: string) => {
    const colors = {
      Current: 'bg-emerald-100 text-emerald-800 dark:bg-emerald-900/30 dark:text-emerald-300',
      Former: 'bg-red-100 text-red-800 dark:bg-red-900/30 dark:text-red-300',
      Founding: 'bg-amber-100 text-amber-800 dark:bg-amber-900/30 dark:text-amber-300'
    };
    return colors[status as keyof typeof colors] || 'bg-gray-100 text-gray-800 dark:bg-gray-700 dark:text-gray-200';
  };

  const accuracy = attempts > 0 ? Math.round((score / attempts) * 100) : 0;
  const filteredData = getFilteredData();

  return (
    <div className="space-y-6">
      {/* Game Header & Stats */}
      <div className="flex flex-col sm:flex-row justify-between items-start sm:items-center gap-4">
        <div className="flex items-center gap-3">
          <div className="w-6 h-6 bg-slate-600 rounded-lg flex items-center justify-center">
            <Globe className="h-4 w-4 text-white" />
          </div>
          <div>
            <h3 className="text-lg font-semibold text-gray-900 dark:text-white">UK International Memberships Triple Match</h3>
            <p className="text-sm text-gray-600 dark:text-gray-300">Match organizations with UK's roles and membership details</p>
          </div>
        </div>

        <div className="flex items-center gap-4">
          <div className="text-center">
            <div className="text-lg font-bold text-slate-600 dark:text-slate-400">{score}/{filteredData.length}</div>
            <div className="text-xs text-gray-500 dark:text-gray-400">Matches</div>
          </div>
          <div className="text-center">
            <div className="text-lg font-bold text-emerald-600 dark:text-emerald-400">{accuracy}%</div>
            <div className="text-xs text-gray-500 dark:text-gray-400">Accuracy</div>
          </div>
        </div>
      </div>

      {/* Filters */}
      <div className="space-y-3">
        <div className="flex flex-wrap gap-2 items-center">
          <Filter className="h-4 w-4 text-gray-500" />
          <span className="text-sm font-medium text-gray-700">Category:</span>
          {categories.map(category => (
            <Badge
              key={category}
              variant={filterCategory === category ? "default" : "outline"}
              className={`cursor-pointer transition-colors ${
                filterCategory === category 
                  ? 'bg-slate-600 text-white' 
                  : 'hover:bg-slate-100'
              }`}
              onClick={() => setFilterCategory(category)}
            >
              {category === 'all' ? 'All Categories' : category}
            </Badge>
          ))}
        </div>

        <div className="flex flex-wrap gap-2 items-center">
          <Filter className="h-4 w-4 text-gray-500" />
          <span className="text-sm font-medium text-gray-700">Status:</span>
          {statuses.map(status => (
            <Badge
              key={status}
              variant={filterStatus === status ? "default" : "outline"}
              className={`cursor-pointer transition-colors ${
                filterStatus === status 
                  ? 'bg-emerald-600 text-white' 
                  : 'hover:bg-emerald-100'
              }`}
              onClick={() => setFilterStatus(status)}
            >
              {status === 'all' ? 'All Statuses' : status}
            </Badge>
          ))}
        </div>
      </div>

      {/* Game Controls */}
      <div className="flex gap-2">
        <Button
          onClick={initializeGame}
          variant="outline"
          size="sm"
          className="flex items-center gap-2"
        >
          <Shuffle className="h-4 w-4" />
          Shuffle
        </Button>
        <Button
          onClick={initializeGame}
          variant="outline"
          size="sm"
          className="flex items-center gap-2"
        >
          <RotateCcw className="h-4 w-4" />
          Reset
        </Button>
      </div>

      {gameComplete && (
        <Card className="border-emerald-200 dark:border-emerald-800 bg-emerald-50 dark:bg-emerald-900/20">
          <CardContent className="p-4 text-center">
            <h3 className="text-lg font-semibold text-emerald-800 dark:text-emerald-300 mb-2">🎉 Congratulations!</h3>
            <p className="text-emerald-700 dark:text-emerald-400">
              You've successfully matched all {filteredData.length} UK international memberships!
            </p>
            <p className="text-sm text-emerald-600 dark:text-emerald-400 mt-1">
              Final Score: {score}/{filteredData.length} ({accuracy}% accuracy)
            </p>
          </CardContent>
        </Card>
      )}

      {/* Matching Game Grid */}
      <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
        {/* Organizations Column */}
        <Card className="bg-white dark:bg-gray-800 border-gray-200 dark:border-gray-700">
          <CardHeader className="pb-3">
            <CardTitle className="text-base flex items-center gap-2 text-gray-900 dark:text-white">
              <div className="w-4 h-4 bg-slate-600 rounded flex items-center justify-center">
                <Globe className="h-3 w-3 text-white" />
              </div>
              Organizations ({filteredData.length})
            </CardTitle>
          </CardHeader>
          <CardContent className="space-y-2">
            {organizations.map((org) => (
              <div key={`org-${org.id}`} className="space-y-1">
                <button
                  onClick={() => handleOrganizationClick(org)}
                  className={getButtonStyle(org, selectedOrganization?.id === org.id)}
                  disabled={matchedItems.has(org.id)}
                >
                  <div className="font-medium text-sm leading-relaxed">{org.organization}</div>
                  <div className="flex gap-1 mt-1 flex-wrap">
                    <Badge className={`text-xs ${getCategoryColor(org.category)}`}>
                      {org.category}
                    </Badge>
                    <Badge className={`text-xs ${getStatusColor(org.status)}`}>
                      {org.status}
                    </Badge>
                    <Badge className="text-xs bg-gray-100 text-gray-800 dark:bg-gray-700 dark:text-gray-200">
                      {org.joinedYear}
                    </Badge>
                  </div>
                </button>
              </div>
            ))}
          </CardContent>
        </Card>

        {/* Roles Column */}
        <Card className="bg-white dark:bg-gray-800 border-gray-200 dark:border-gray-700">
          <CardHeader className="pb-3">
            <CardTitle className="text-base flex items-center gap-2 text-gray-900 dark:text-white">
              <div className="w-4 h-4 bg-emerald-600 rounded flex items-center justify-center">
                <Globe className="h-3 w-3 text-white" />
              </div>
              UK's Role ({filteredData.length})
            </CardTitle>
          </CardHeader>
          <CardContent className="space-y-2">
            {roles.map((role) => (
              <button
                key={`role-${role.id}`}
                onClick={() => handleRoleClick(role)}
                className={getButtonStyle(role, selectedRole?.id === role.id)}
                disabled={matchedItems.has(role.id)}
              >
                <div className="font-bold text-center text-sm">
                  {role.role}
                </div>
                <Badge className={`text-xs w-full justify-center mt-1 ${getStatusColor(role.status)}`}>
                  {role.status} Member
                </Badge>
              </button>
            ))}
          </CardContent>
        </Card>

        {/* Details Column */}
        <Card className="bg-white dark:bg-gray-800 border-gray-200 dark:border-gray-700">
          <CardHeader className="pb-3">
            <CardTitle className="text-base flex items-center gap-2 text-gray-900 dark:text-white">
              <div className="w-4 h-4 bg-violet-600 rounded flex items-center justify-center">
                <Globe className="h-3 w-3 text-white" />
              </div>
              Membership Details ({filteredData.length})
            </CardTitle>
          </CardHeader>
          <CardContent className="space-y-2">
            {details.map((detail) => (
              <button
                key={`detail-${detail.id}`}
                onClick={() => handleDetailClick(detail)}
                className={getButtonStyle(detail, selectedDetail?.id === detail.id)}
                disabled={matchedItems.has(detail.id)}
              >
                <div className="font-medium text-sm leading-relaxed">
                  {detail.details}
                </div>
              </button>
            ))}
          </CardContent>
        </Card>
      </div>

      {/* Instructions */}
      <Card className="bg-slate-50 dark:bg-slate-900/20 border-slate-200 dark:border-slate-800">
        <CardContent className="p-4">
          <h4 className="font-semibold text-slate-800 dark:text-slate-300 mb-2">How to Play:</h4>
          <ul className="text-sm text-slate-700 dark:text-slate-400 space-y-1">
            <li>• Select one organization, one UK role, and one detail that all belong together</li>
            <li>• All three selections must match the same international membership</li>
            <li>• Correct matches turn emerald and celebrate with confetti</li>
            <li>• Use category and status filters to focus on specific types of memberships</li>
            <li>• Learn about UK's role in global governance and international cooperation!</li>
          </ul>
        </CardContent>
      </Card>
    </div>
  );
}

server/services/rag-content-generator.ts.content:
import OpenAI from 'openai';
import { db } from '../db';
import { books, bookChunks, generatedTopics, generatedTests } from '../../shared/schema';
import { eq, desc } from 'drizzle-orm';

const openai = new OpenAI({
  apiKey: process.env.OPENAI_API_KEY,
});

export class RAGContentGenerator {
  private async generateEmbedding(text: string): Promise<number[]> {
    try {
      const response = await openai.embeddings.create({
        model: 'text-embedding-3-small',
        input: text,
      });
      return response.data[0].embedding;
    } catch (error) {
      console.error('Error generating embedding:', error);
      throw new Error('Failed to generate embedding');
    }
  }

  private async findRelevantChunks(query: string, limit: number = 5): Promise<any[]> {
    try {
      // Generate embedding for the query
      const queryEmbedding = await this.generateEmbedding(query);

      // For now, we'll get all chunks and do a simple text search
      // In production, you'd use pgvector for proper similarity search
      const allChunks = await db.select()
        .from(bookChunks)
        .orderBy(desc(bookChunks.createdAt))
        .limit(limit * 3); // Get more chunks for better filtering

      // Simple text similarity (in production, use vector similarity)
      const relevantChunks = allChunks.filter(chunk => 
        chunk.content.toLowerCase().includes(query.toLowerCase()) ||
        query.toLowerCase().split(' ').some(word => 
          chunk.content.toLowerCase().includes(word)
        )
      ).slice(0, limit);

      return relevantChunks;
    } catch (error) {
      console.error('Error finding relevant chunks:', error);
      return [];
    }
  }

  public async generateTimelineContent(topic: string): Promise<any> {
    try {
      console.log(`Generating timeline content for topic: ${topic}`);

      // Find relevant content from the books
      const relevantChunks = await this.findRelevantChunks(topic, 5);

      if (relevantChunks.length === 0) {
        throw new Error('No relevant content found for the topic');
      }

      // Combine relevant content
      const contextContent = relevantChunks.map(chunk => chunk.content).join('\n\n');

      const prompt = `
        Based on the following Life in the UK test study material, create a comprehensive timeline entry for the topic: "${topic}"

        Study Material Context:
        ${contextContent}

        Please create a timeline entry with:
        1. Title (clear and specific)
        2. Year/Date (if available)
        3. Description (2-3 sentences explaining what happened)
        4. Details (more comprehensive explanation)
        5. Category (parliament, documents, voting_rights, territories, trades, etc.)
        6. Importance (1-5 scale, where 5 is most important)
        7. Key Figures (notable people involved)
        8. Timeline Topic (which timeline this belongs to)

        Focus on accuracy and testable information that would appear on the Life in the UK test.
        Make sure the content is historically accurate and educationally valuable.
      `;

      const response = await openai.chat.completions.create({
        model: 'gpt-4o-mini',
        messages: [
          {
            role: 'system',
            content: 'You are an expert in UK history and citizenship test preparation. Create accurate, educational timeline entries based on official study materials.'
          },
          {
            role: 'user',
            content: prompt
          }
        ],
        temperature: 0.3,
      });

      const timelineContent = response.choices[0].message.content;

      // Parse the response to extract structured data
      try {
        // Clean up the response to extract JSON
        let cleanText = timelineContent || '{}';

        // Remove markdown code blocks if present
        if (cleanText.includes('```json')) {
          cleanText = cleanText.split('```json')[1].split('```')[0];
        } else if (cleanText.includes('```')) {
          cleanText = cleanText.split('```')[1].split('```')[0];
        }

        const parsedContent = JSON.parse(cleanText);
        return {
          title: parsedContent.title || topic,
          year: parsedContent.year || new Date().getFullYear(),
          description: parsedContent.description || '',
          details: parsedContent.details || '',
          category: parsedContent.category || 'general',
          importance: parsedContent.importance || 3,
          keyFigures: parsedContent.keyFigures || '',
          timelineTopic: parsedContent.timelineTopic || 'uk_history',
          rawContent: timelineContent
        };
      } catch (parseError) {
        // If JSON parsing fails, return the raw content
        return {
          title: topic,
          year: new Date().getFullYear(),
          description: timelineContent || '',
          details: timelineContent || '',
          category: 'general',
          importance: 3,
          keyFigures: '',
          timelineTopic: 'uk_history',
          rawContent: timelineContent
        };
      }
    } catch (error) {
      console.error('Error generating timeline content:', error);
      throw error;
    }
  }

  public async generatePracticeQuestions(topic: string, difficulty: string = 'intermediate'): Promise<any[]> {
    try {
      console.log(`Generating practice questions for topic: ${topic}`);

      // Find relevant content from the books
      const relevantChunks = await this.findRelevantChunks(topic, 5);

      if (relevantChunks.length === 0) {
        throw new Error('No relevant content found for the topic');
      }

      // Combine relevant content
      const contextContent = relevantChunks.map(chunk => chunk.content).join('\n\n');

      const prompt = `
        Based on the following Life in the UK test study material, generate 5 practice questions for the topic: "${topic}"
        Difficulty Level: ${difficulty}

        Study Material Context:
        ${contextContent}

        Create 5 multiple choice questions with:
        1. Question text (clear and specific)
        2. 4 answer options (A, B, C, D)
        3. Correct answer (A, B, C, or D)
        4. Explanation (why the answer is correct)
        5. Difficulty level (beginner/intermediate/advanced)

        Focus on facts, dates, laws, and important information that appears on the actual Life in the UK test.
        Make sure questions are accurate and testable.
        Return the response as a JSON array of question objects.
      `;

      const response = await openai.chat.completions.create({
        model: 'gpt-4o-mini',
        messages: [
          {
            role: 'system',
            content: 'You are an expert in creating Life in the UK test questions. Generate accurate, testable questions based on official study materials.'
          },
          {
            role: 'user',
            content: prompt
          }
        ],
        temperature: 0.3,
      });

      const questionsText = response.choices[0].message.content;

      try {
        // Clean up the response to extract JSON
        let cleanText = questionsText || '[]';

        // Remove markdown code blocks if present
        if (cleanText.includes('```json')) {
          const jsonStart = cleanText.indexOf('```json') + 7;
          const jsonEnd = cleanText.indexOf('```', jsonStart);
          if (jsonEnd > jsonStart) {
            cleanText = cleanText.substring(jsonStart, jsonEnd).trim();
          }
        } else if (cleanText.includes('```')) {
          const codeStart = cleanText.indexOf('```') + 3;
          const codeEnd = cleanText.indexOf('```', codeStart);
          if (codeEnd > codeStart) {
            cleanText = cleanText.substring(codeStart, codeEnd).trim();
          }
        }

        const questions = JSON.parse(cleanText);
        return questions;
      } catch (parseError) {
        console.error('Error parsing questions JSON:', parseError);
        console.error('Raw response:', questionsText);
        console.error('Cleaned text:', cleanText);
        return [];
      }
    } catch (error) {
      console.error('Error generating practice questions:', error);
      throw error;
    }
  }

  public async generateStudyGuide(topic: string): Promise<any> {
    try {
      console.log(`Generating study guide for topic: ${topic}`);

      // Find relevant content from the books
      const relevantChunks = await this.findRelevantChunks(topic, 8);

      if (relevantChunks.length === 0) {
        throw new Error('No relevant content found for the topic');
      }

      // Combine relevant content
      const contextContent = relevantChunks.map(chunk => chunk.content).join('\n\n');

      const prompt = `
        Based on the following Life in the UK test study material, create a comprehensive study guide for the topic: "${topic}"

        Study Material Context:
        ${contextContent}

        Create a study guide with:
        1. Overview (brief introduction to the topic)
        2. Key Facts (important facts and figures)
        3. Important Dates (if applicable)
        4. Key People (notable figures involved)
        5. Key Concepts (important ideas and principles)
        6. Test Tips (what to focus on for the exam)
        7. Practice Points (what to study and remember)

        Make the content clear, accurate, and focused on what students need to know for the Life in the UK test.
        Use bullet points and clear headings for easy reading.
      `;

      const response = await openai.chat.completions.create({
        model: 'gpt-4o-mini',
        messages: [
          {
            role: 'system',
            content: 'You are an expert in UK citizenship test preparation. Create comprehensive, accurate study guides based on official materials.'
          },
          {
            role: 'user',
            content: prompt
          }
        ],
        temperature: 0.3,
      });

      const studyGuide = response.choices[0].message.content;

      return {
        topic: topic,
        content: studyGuide,
        generatedAt: new Date().toISOString(),
        sourceChunks: relevantChunks.length
      };
    } catch (error) {
      console.error('Error generating study guide:', error);
      throw error;
    }
  }

  public async getAvailableTopics(): Promise<string[]> {
    try {
      const topics = await db.select({
        title: generatedTopics.title,
        description: generatedTopics.description,
        difficulty: generatedTopics.difficulty
      })
      .from(generatedTopics)
      .orderBy(desc(generatedTopics.createdAt));

      return topics.map(topic => topic.title);
    } catch (error) {
      console.error('Error getting available topics:', error);
      return [];
    }
  }

  public async getBookStats(): Promise<any> {
    try {
      const stats = await db.select({
        totalBooks: books.id,
        totalChunks: bookChunks.id,
        totalTopics: generatedTopics.id,
        totalTests: generatedTests.id
      })
      .from(books)
      .leftJoin(bookChunks, eq(books.id, bookChunks.bookId))
      .leftJoin(generatedTopics, eq(books.id, generatedTopics.bookId))
      .leftJoin(generatedTests, eq(books.id, generatedTests.bookId));

      return {
        totalBooks: stats.length,
        totalChunks: stats.reduce((sum, stat) => sum + (stat.totalChunks ? 1 : 0), 0),
        totalTopics: stats.reduce((sum, stat) => sum + (stat.totalTopics ? 1 : 0), 0),
        totalTests: stats.reduce((sum, stat) => sum + (stat.totalTests ? 1 : 0), 0)
      };
    } catch (error) {
      console.error('Error getting book stats:', error);
      return { totalBooks: 0, totalChunks: 0, totalTopics: 0, totalTests: 0 };
    }
  }
}

export const ragContentGenerator = new RAGContentGenerator();

client/src/components/enhanced-video-lib.content:
import { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Progress } from "@/components/ui/progress";
import { 
  Play, 
  Clock, 
  Tag, 
  Search, 
  Filter, 
  Grid, 
  List, 
  Download,
  CheckCircle,
  PlayCircle,
  Lock,
  Youtube,
  Video,
  FileText,
  Image,
  BookOpen,
  Star,
  TrendingUp,
  Volume2,
  VolumeX
} from "lucide-react";
import type { VideoModule, UserVideoProgress } from "@shared/schema";

interface EnhancedVideoLibraryProps {
  userId: string;
  onVideoSelect?: (video: VideoModule) => void;
}

const categories = [
  { value: "all", label: "All Categories" },
  { value: "Government", label: "Government & Politics" },
  { value: "History", label: "History" },
  { value: "Culture", label: "Culture & Society" },
  { value: "Geography", label: "Geography" },
  { value: "Law", label: "Law & Justice" },
  { value: "Education", label: "Education" },
  { value: "Sports", label: "Sports & Recreation" },
  { value: "Arts", label: "Arts & Literature" },
  { value: "Economy", label: "Economy" },
  { value: "Society", label: "Society & Community" }
];

export default function EnhancedVideoLibrary({ userId, onVideoSelect }: EnhancedVideoLibraryProps) {
  const [searchTerm, setSearchTerm] = useState("");
  const [selectedCategory, setSelectedCategory] = useState("all");
  const [viewMode, setViewMode] = useState<"grid" | "list">("grid");
  const [selectedVideo, setSelectedVideo] = useState<VideoModule | null>(null);
  const [isPlayerOpen, setIsPlayerOpen] = useState(false);
  const [speaking, setSpeaking] = useState<string | null>(null);

  const { data: videos = [], isLoading } = useQuery<VideoModule[]>({
    queryKey: ["/api/videos"],
  });

  const { data: progress = [] } = useQuery<UserVideoProgress[]>({
    queryKey: ["/api/videos/progress", userId],
  });

  // Fetch resources for the selected video
  const { data: videoResources = [], isLoading: resourcesLoading } = useQuery({
    queryKey: ["video-resources", selectedVideo?.id],
    queryFn: async () => {
      if (!selectedVideo?.id) return [];
      const response = await apiRequest("GET", `/api/admin/videos/${selectedVideo.id}/resources`);
      if (!response.ok) return [];
      return response.json();
    },
    enabled: !!selectedVideo?.id,
  });

  // Fetch detailed content for the selected video
  const { data: videoContent, isLoading: contentLoading } = useQuery({
    queryKey: ["video-content", selectedVideo?.id],
    queryFn: async () => {
      if (!selectedVideo?.id) return null;
      const response = await apiRequest("GET", `/api/admin/videos/${selectedVideo.id}/content`);
      if (!response.ok) return null;
      return response.json();
    },
    enabled: !!selectedVideo?.id,
  });

  const handleVideoClick = (video: VideoModule) => {
    setSelectedVideo(video);
    setIsPlayerOpen(true);
    onVideoSelect?.(video);
  };

  const handleClosePlayer = () => {
    setIsPlayerOpen(false);
    setSelectedVideo(null);
    if (speaking) {
      speechSynthesis.cancel();
      setSpeaking(null);
    }
  };

  const speakText = (text: string, videoId: string) => {
    if (speaking) {
      speechSynthesis.cancel();
      setSpeaking(null);
    } else {
      const utterance = new SpeechSynthesisUtterance(text);
      utterance.rate = 0.8;
      utterance.pitch = 1;
      utterance.volume = 0.8;

      // Try to use British voice
      const voices = speechSynthesis.getVoices();
      const britishVoice = voices.find(voice => 
        voice.lang.includes('en-GB') || 
        voice.name.includes('British') ||
        voice.name.includes('UK')
      );

      if (britishVoice) {
        utterance.voice = britishVoice;
      }

      utterance.onend = () => setSpeaking(null);
      utterance.onerror = () => setSpeaking(null);

      speechSynthesis.speak(utterance);
      setSpeaking(videoId);
    }
  };

  const getVideoProgress = (videoId: string) => {
    return progress.find(p => p.videoId === videoId);
  };

  const getVideoThumbnail = (video: VideoModule) => {
    if (video.videoType === 'youtube') {
      const videoId = video.videoUrl.match(/(?:youtube\.com\/watch\?v=|youtu\.be\/)([^&\n?#]+)/)?.[1];
      return videoId ? `https://img.youtube.com/vi/${videoId}/maxresdefault.jpg` : null;
    }
    return null;
  };

  const filteredVideos = videos.filter(video => {
    const matchesSearch = video.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         video.description.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         (video.tags && typeof video.tags === 'string' && video.tags.toLowerCase().includes(searchTerm.toLowerCase()));

    const matchesCategory = selectedCategory === "all" || video.category === selectedCategory;

    return matchesSearch && matchesCategory;
  });

  const formatDuration = (seconds: number) => {
    const hours = Math.floor(seconds / 3600);
    const minutes = Math.floor((seconds % 3600) / 60);
    const secs = seconds % 60;

    if (hours > 0) {
      return `${hours}:${minutes.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}`;
    }
    return `${minutes}:${secs.toString().padStart(2, '0')}`;
  };

  const getCategoryColor = (category: string) => {
    const colors: Record<string, string> = {
      "Government": "bg-blue-100 text-blue-800 dark:bg-blue-900 dark:text-blue-200",
      "History": "bg-amber-100 text-amber-800 dark:bg-amber-900 dark:text-amber-200",
      "Culture": "bg-purple-100 text-purple-800 dark:bg-purple-900 dark:text-purple-200",
      "Geography": "bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-200",
      "Law": "bg-red-100 text-red-800 dark:bg-red-900 dark:text-red-200",
      "Education": "bg-indigo-100 text-indigo-800 dark:bg-indigo-900 dark:text-indigo-200",
      "Sports": "bg-orange-100 text-orange-800 dark:bg-orange-900 dark:text-orange-200",
      "Arts": "bg-pink-100 text-pink-800 dark:bg-pink-900 dark:text-pink-200",
      "Economy": "bg-emerald-100 text-emerald-800 dark:bg-emerald-900 dark:text-emerald-200",
      "Society": "bg-cyan-100 text-cyan-800 dark:bg-cyan-900 dark:text-cyan-200",
    };
    return colors[category] || "bg-gray-100 text-gray-800 dark:bg-gray-900 dark:text-gray-200";
  };

  if (isLoading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="text-center">
          <div className="w-8 h-8 border-2 border-blue-600 border-t-transparent rounded-full animate-spin mx-auto mb-4"></div>
          <p className="text-gray-600 dark:text-gray-400">Loading video library...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between gap-3 sm:gap-4">
        <div>
          <h2 className="text-xl sm:text-2xl font-bold text-gray-900 dark:text-white">Educational Video Library</h2>
          <p className="text-sm sm:text-base text-gray-600 dark:text-gray-400">
            {filteredVideos.length} video{filteredVideos.length !== 1 ? 's' : ''} available
          </p>
        </div>

        <div className="flex items-center gap-2">
          <Button
            variant={viewMode === "grid" ? "default" : "outline"}
            size="sm"
            onClick={() => setViewMode("grid")}
            className={`${viewMode === "grid" ? "bg-blue-600 hover:bg-blue-700 dark:bg-blue-600 dark:hover:bg-blue-700" : "border-gray-300 dark:border-gray-600 text-gray-700 dark:text-gray-300 hover:bg-gray-50 dark:hover:bg-gray-700"} h-8 w-8 p-0`}
          >
            <Grid className="h-4 w-4" />
          </Button>
          <Button
            variant={viewMode === "list" ? "default" : "outline"}
            size="sm"
            onClick={() => setViewMode("list")}
            className={`${viewMode === "list" ? "bg-blue-600 hover:bg-blue-700 dark:bg-blue-600 dark:hover:bg-blue-700" : "border-gray-300 dark:border-gray-600 text-gray-700 dark:text-gray-300 hover:bg-gray-50 dark:hover:bg-gray-700"} h-8 w-8 p-0`}
          >
            <List className="h-4 w-4" />
          </Button>
        </div>
      </div>

      {/* Filters */}
      <div className="flex flex-col sm:flex-row gap-3 sm:gap-4">
        <div className="flex-1">
          <div className="relative">
            <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 h-4 w-4" />
            <Input
              placeholder="Search videos, descriptions, or tags..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="pl-10 h-9 sm:h-10 border-gray-300 dark:border-gray-600 text-gray-900 dark:text-white bg-white dark:bg-gray-800 text-sm"
            />
          </div>
        </div>

        <Select value={selectedCategory} onValueChange={setSelectedCategory}>
          <SelectTrigger className="w-full sm:w-48 h-9 sm:h-10 border-gray-300 dark:border-gray-600 text-gray-900 dark:text-white bg-white dark:bg-gray-800 text-sm">
            <SelectValue placeholder="Filter by category" />
          </SelectTrigger>
          <SelectContent className="bg-white dark:bg-gray-800 border-gray-200 dark:border-gray-700">
            {categories.map((category) => (
              <SelectItem key={category.value} value={category.value} className="text-gray-900 dark:text-white hover:bg-gray-50 dark:hover:bg-gray-700 text-sm">
                {category.label}
              </SelectItem>
            ))}
          </SelectContent>
        </Select>
      </div>

      {/* Video Grid/List */}
      {filteredVideos.length === 0 ? (
        <div className="text-center py-12">
          <Video className="h-12 w-12 text-gray-400 dark:text-gray-500 mx-auto mb-4" />
          <h3 className="text-lg font-medium text-gray-900 dark:text-white mb-2">No videos found</h3>
          <p className="text-gray-600 dark:text-gray-400">
            {searchTerm || selectedCategory !== "all" 
              ? "Try adjusting your search or filters" 
              : "No videos have been uploaded yet"}
          </p>
        </div>
      ) : (
        <div className={viewMode === "grid" 
          ? "grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-4 sm:gap-6" 
          : "space-y-3 sm:space-y-4"
        }>
          {filteredVideos.map((video) => {
            const videoProgress = getVideoProgress(video.id);
            const thumbnail = getVideoThumbnail(video);
            const isCompleted = videoProgress?.completed;
            const progressPercent = videoProgress?.watchTime || 0;

            return (
              <Card 
                key={video.id} 
                className={`group cursor-pointer transition-all duration-200 hover:shadow-lg hover:scale-[1.02] !bg-white dark:!bg-gray-800 !border-gray-200 dark:!border-gray-700 shadow-sm ${
                  isCompleted ? 'ring-2 ring-green-200 dark:ring-green-800' : ''
                }`}
                onClick={() => handleVideoClick(video)}
              >
                <CardHeader className="p-0">
                  <div className="relative">
                    {thumbnail ? (
                      <img
                        src={thumbnail}
                        alt={video.title}
                        className="w-full h-40 sm:h-48 object-cover rounded-t-lg"
                      />
                    ) : (
                      <div className="w-full h-40 sm:h-48 bg-gradient-to-br from-blue-500 to-purple-600 rounded-t-lg flex items-center justify-center">
                        {video.videoType === 'youtube' ? (
                          <Youtube className="h-10 w-10 sm:h-12 sm:w-12 text-white" />
                        ) : (
                          <Video className="h-10 w-10 sm:h-12 sm:w-12 text-white" />
                        )}
                      </div>
                    )}

                    {/* Progress overlay */}
                    {videoProgress && (
                      <div className="absolute bottom-0 left-0 right-0 bg-black/50 text-white p-2">
                        <Progress value={progressPercent} className="h-1 mb-1" />
                        <div className="flex items-center justify-between text-xs">
                          <span>{Math.round(progressPercent)}% watched</span>
                          {isCompleted && <CheckCircle className="h-3 w-3 text-green-400" />}
                        </div>
                      </div>
                    )}

                    {/* Play button overlay */}
                    <div className="absolute inset-0 flex items-center justify-center opacity-0 group-hover:opacity-100 transition-opacity bg-black/20 rounded-t-lg">
                      <PlayCircle className="h-12 w-12 text-white" />
                    </div>

