                          {videoContent.content.keyPoints && (
                            <div className="mb-6">
                              <h4 className="text-md font-medium text-gray-900 dark:text-gray-100 mb-3 flex items-center">
                                <Star className="h-4 w-4 mr-2 text-yellow-500" />
