                            <div className="flex items-center space-x-4 text-sm text-gray-600 dark:text-gray-400">
                              <span className="font-medium">Difficulty:</span>
                              <Badge variant="outline" className="capitalize">
                                {videoContent.content.difficulty}
                              </Badge>
                            </div>
