import { useState } from "react";
import { useLocation } from "wouter";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import MobileNav from "@/components/mobile-nav";
import FloatingChatbot from "@/components/floating-chatbot";
import { Clock, Target, CheckCircle, BookOpen, Users, Filter, Star, TrendingUp, BarChart3 } from "lucide-react";
import { 
  useEnhancedMockTests, 
  getDifficultyColor, 
  getDifficultyLabel,
  type EnhancedTest 
} from "@/hooks/useEnhancedTests";

export default function EnhancedMockTestsPage() {
  const [, setLocation] = useLocation();
  const { data: mockTests, isLoading, error } = useEnhancedMockTests();

  // Filter state
  const [difficultyFilter, setDifficultyFilter] = useState<string>("all");
  const [sortBy, setSortBy] = useState<string>("category");

  // Filter logic with safety check
  const filteredTests = (() => {
    // Safety check to ensure mockTests is an array
    if (!Array.isArray(mockTests)) {
      console.warn('filteredTests received non-array data:', mockTests);
      return [];
    }

    return mockTests.filter((test) => {
      let matchesDifficulty = true;
      if (difficultyFilter !== "all") {
        const filterValue = parseInt(difficultyFilter);
        if (filterValue === 1) {
          // Beginner: difficulty 1
          matchesDifficulty = test.difficulty === 1;
        } else if (filterValue === 3) {
          // Intermediate: difficulty 2, 3, 4
          matchesDifficulty = test.difficulty >= 2 && test.difficulty <= 4;
        } else if (filterValue === 5) {
          // Expert: difficulty 5
          matchesDifficulty = test.difficulty === 5;
        }
      }

      return matchesDifficulty;
    });
  })();

  // Sort logic
  const sortedTests = [...filteredTests].sort((a, b) => {
    switch (sortBy) {
      case "difficulty":
        return a.difficulty - b.difficulty;
      case "category":
        return (a.category || "").localeCompare(b.category || "");
      case "order":
      default:
        return a.orderIndex - b.orderIndex;
    }
  });

  if (isLoading) {
    return (
      <div className="min-h-screen bg-gray-50 dark:bg-gray-900 p-4">
        <div className="container mx-auto max-w-6xl">
          <div className="text-center py-8 sm:py-12">
            <div className="animate-spin rounded-full h-8 w-8 sm:h-12 sm:w-12 border-b-2 border-blue-600 mx-auto"></div>
            <p className="mt-4 text-sm sm:text-base text-gray-600 dark:text-gray-400">Loading enhanced mock tests...</p>
          </div>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-h-screen bg-gray-50 dark:bg-gray-900 p-4">
        <div className="container mx-auto max-w-6xl">
          <div className="text-center py-8 sm:py-12">
            <div className="text-red-600 dark:text-red-400 mb-4">
              <Target className="h-12 w-12 sm:h-16 sm:w-16 mx-auto" />
            </div>
            <h3 className="text-lg sm:text-xl font-semibold text-gray-900 dark:text-white mb-2">Error Loading Tests</h3>
            <p className="text-sm sm:text-base text-gray-600 dark:text-gray-400 mb-4">There was an error loading the mock tests. Please try again.</p>
            <Button 
              onClick={() => window.location.reload()} 
              className="w-full sm:w-auto"
            >
              Retry
            </Button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50 dark:bg-gray-900">
      {/* Enhanced Header */}
      <div className="bg-white dark:bg-gray-800 shadow-sm border-b border-gray-200 dark:border-gray-700">
        <div className="container mx-auto max-w-6xl px-4 py-4 sm:py-6">
          <div className="flex flex-col space-y-4 sm:flex-row sm:items-center sm:justify-between sm:space-y-0">
            <div className="flex-1">
                <h1 className="text-2xl sm:text-3xl font-bold text-gray-900 dark:text-white">Mock Tests</h1>
              <p className="text-sm sm:text-base text-gray-600 dark:text-gray-400 mt-1 sm:mt-2">
                Official-style mock tests with comprehensive analytics and performance tracking
              </p>
            </div>
            <div className="flex flex-col space-y-2 sm:flex-row sm:space-y-0 sm:space-x-3">
              <Button 
                onClick={() => setLocation("/practice-tests")}
                className="w-full sm:w-auto bg-blue-600 hover:bg-blue-700 dark:bg-blue-600 dark:hover:bg-blue-700"
              >
                Practice Tests
              </Button>
              <Button 
                variant="outline" 
                onClick={() => setLocation("/dashboard")}
                className="w-full sm:w-auto border-gray-300 dark:border-gray-600 text-gray-700 dark:text-gray-300 hover:bg-gray-50 dark:hover:bg-gray-700"
              >
                Dashboard
              </Button>
            </div>
          </div>
        </div>
      </div>

      {/* Enhanced Stats Overview */}
      <div className="container mx-auto max-w-6xl p-4">
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-4 mb-8">
          <Card className="!bg-white dark:!bg-gray-800 !border-gray-200 dark:!border-gray-700 shadow-sm">
            <CardContent className="p-6">
              <div className="flex items-center space-x-2">
                <Target className="h-8 w-8 text-blue-600 dark:text-blue-400" />
                <div>
                  <p className="text-2xl font-bold text-gray-900 dark:text-white">{Array.isArray(mockTests) ? mockTests.length : 0}</p>
                  <p className="text-sm text-gray-600 dark:text-gray-400">Total Tests</p>
                </div>
              </div>
            </CardContent>
          </Card>
          <Card className="!bg-white dark:!bg-gray-800 !border-gray-200 dark:!border-gray-700 shadow-sm">
            <CardContent className="p-6">
              <div className="flex items-center space-x-2">
                <CheckCircle className="h-8 w-8 text-green-600 dark:text-green-400" />
                <div>
                  <p className="text-2xl font-bold text-gray-900 dark:text-white">
                    {Array.isArray(mockTests) && mockTests.length > 0 ? Math.round(mockTests.reduce((sum, test) => sum + (test.questions?.length || 0), 0) / mockTests.length) : 0}
                  </p>
                  <p className="text-sm text-gray-600 dark:text-gray-400">Avg Questions</p>
                </div>
              </div>
            </CardContent>
          </Card>
          <Card className="!bg-white dark:!bg-gray-800 !border-gray-200 dark:!border-gray-700 shadow-sm">
            <CardContent className="p-6">
              <div className="flex items-center space-x-2">
                <Clock className="h-8 w-8 text-orange-600 dark:text-orange-400" />
                <div>
                  <p className="text-2xl font-bold text-gray-900 dark:text-white">45</p>
                  <p className="text-sm text-gray-600 dark:text-gray-400">Minutes</p>
                </div>
              </div>
            </CardContent>
          </Card>
          <Card className="!bg-white dark:!bg-gray-800 !border-gray-200 dark:!border-gray-700 shadow-sm">
            <CardContent className="p-6">
              <div className="flex items-center space-x-2">
                <BarChart3 className="h-8 w-8 text-purple-600 dark:text-purple-400" />
                <div>
                  <p className="text-2xl font-bold text-gray-900 dark:text-white">75%</p>
                  <p className="text-sm text-gray-600 dark:text-gray-400">Pass Mark</p>
                </div>
              </div>
            </CardContent>
          </Card>
        </div>

        {/* Enhanced Filters */}
        <div className="bg-white dark:bg-gray-800 rounded-lg shadow-sm border border-gray-200 dark:border-gray-700 p-4 sm:p-6 mb-8">
          <div className="flex flex-col space-y-4 lg:flex-row lg:space-y-0 lg:gap-4 lg:items-center">
            <div className="flex flex-col space-y-3 sm:flex-row sm:space-y-0 sm:gap-4 flex-1">
              <Select value={difficultyFilter} onValueChange={setDifficultyFilter}>
                <SelectTrigger className="w-full sm:w-40">
                  <SelectValue placeholder="Level" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">All Levels</SelectItem>
                  <SelectItem value="1">Beginner</SelectItem>
                  <SelectItem value="3">Intermediate</SelectItem>
                  <SelectItem value="5">Expert</SelectItem>
                </SelectContent>
              </Select>

              <Select value={sortBy} onValueChange={setSortBy}>
                <SelectTrigger className="w-full sm:w-40">
                  <SelectValue placeholder="Sort by" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="order">Order</SelectItem>
                  <SelectItem value="difficulty">Difficulty</SelectItem>
                  <SelectItem value="category">CategorySelect</SelectItem>
                </SelectContent>
              </Select>
            </div>

            {/* Reset Button */}
            <div className="flex justify-center lg:justify-end">
              <Button 
                onClick={() => {
                  setDifficultyFilter("all");
                  setSortBy("category");
                }}
                variant="outline"
                className="w-full sm:w-auto"
              >
                Reset Filters
              </Button>
            </div>
          </div>
        </div>

        {/* Enhanced Info Banner */}
        <div className="mb-6">
          <div className="bg-blue-50 dark:bg-blue-900/20 border border-blue-200 dark:border-blue-700 rounded-lg p-3 sm:p-4">
            <div className="flex items-start">
              <Users className="h-4 w-4 sm:h-5 sm:w-5 text-blue-600 dark:text-blue-400 mr-2 mt-0.5 flex-shrink-0" />
              <div>
                  <h3 className="font-medium text-blue-900 dark:text-blue-100 mb-1 text-sm sm:text-base">Mock Test Experience</h3>
                <p className="text-blue-700 dark:text-blue-300 text-xs sm:text-sm">
                  These enhanced mock tests provide comprehensive analytics, detailed performance tracking, 
                  and advanced question management. Perfect for final preparation with real-time feedback 
                  and detailed explanations for every question.
                </p>
              </div>
            </div>
          </div>
        </div>

        {/* Enhanced Mock Tests Grid */}
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-4 sm:gap-6">
          {sortedTests.map((test) => (
            <Card key={test.id} className="hover:shadow-lg transition-all duration-200 !bg-white dark:!bg-gray-800 !border-gray-200 dark:!border-gray-700 border-2 hover:border-blue-200 dark:hover:border-blue-600 shadow-sm">
              <CardHeader className="pb-3">
                <div className="flex items-center justify-between mb-2">
                  <Badge className={`${getDifficultyColor(test.difficulty)} text-xs`}>
                    {getDifficultyLabel(test.difficulty)}
                  </Badge>
                  <span className="text-xs text-gray-500 dark:text-gray-400">#{test.orderIndex}</span>
                </div>
                <CardTitle className="text-base sm:text-lg text-gray-900 dark:text-white line-clamp-2">{test.title}</CardTitle>
                <CardDescription className="text-xs sm:text-sm text-gray-600 dark:text-gray-400 line-clamp-2">
                  {test.description}
                </CardDescription>
              </CardHeader>
              <CardContent className="pt-0">
                <div className="space-y-3 sm:space-y-4">
                  <div className="flex flex-col space-y-2 sm:flex-row sm:items-center sm:justify-between sm:space-y-0 text-xs sm:text-sm text-gray-600 dark:text-gray-400">
                    <div className="flex items-center">
                      <Clock className="h-3 w-3 sm:h-4 sm:w-4 mr-1 flex-shrink-0" />
                      <span>45 minutes</span>
                    </div>
                    <div className="flex items-center">
                      <Target className="h-3 w-3 sm:h-4 sm:w-4 mr-1 flex-shrink-0" />
                      <span>{test.questions?.length || 0} questions</span>
                    </div>
                  </div>

                  <div className="text-xs text-gray-500 dark:text-gray-400">
                    <p>Pass mark: {Math.ceil((test.questions?.length || 0) * 0.75)}/{test.questions?.length || 0} (75%)</p>
                  </div>

                  {/* Enhanced analytics indicators if available */}
                  {test.totalAttempts !== undefined && (
                    <div className="flex flex-col space-y-1 sm:flex-row sm:items-center sm:justify-between sm:space-y-0 text-xs text-gray-500 dark:text-gray-400">
                      <span className="flex items-center">
                        <TrendingUp className="h-3 w-3 mr-1 flex-shrink-0" />
                        <span>{test.totalAttempts} attempts</span>
                      </span>
                      {test.averageScore !== undefined && (
                        <span className="flex items-center">
                          <Star className="h-3 w-3 mr-1 flex-shrink-0" />
                          <span>{test.averageScore}% avg</span>
                        </span>
                      )}
                    </div>
                  )}

                  <Button 
                    className="w-full mt-3 sm:mt-4 bg-blue-600 hover:bg-blue-700 dark:bg-blue-600 dark:hover:bg-blue-700 text-sm sm:text-base" 
                    onClick={() => setLocation(`/mock-test/${test.id}`)}
                  >
                    Start Mock Test
                  </Button>
                </div>
              </CardContent>
            </Card>
          ))}
        </div>

        {!sortedTests.length && !isLoading && (
          <div className="text-center py-8 sm:py-12 px-4">
            <Target className="h-12 w-12 sm:h-16 sm:w-16 text-gray-400 dark:text-gray-500 mx-auto mb-4" />
            <h3 className="text-lg sm:text-xl font-medium text-gray-900 dark:text-white mb-2">No Mock Tests Found</h3>
            <p className="text-sm sm:text-base text-gray-600 dark:text-gray-400 max-w-md mx-auto">
              {difficultyFilter !== "all" 
                ? "Try adjusting your filters to see more tests."
                : "Mock tests are being prepared. Please check back soon."
              }
            </p>
            {(difficultyFilter !== "all") && (
              <Button 
                onClick={() => {
                  setDifficultyFilter("all");
                }}
                className="mt-4 w-full sm:w-auto"
                variant="outline"
              >
                Clear Filters
              </Button>
            )}
          </div>
        )}
      </div>

      <MobileNav />
      <FloatingChatbot />

      {/* Add padding bottom for mobile navigation */}
      <div className="h-16 sm:h-20 md:hidden"></div>
    </div>
  );
}

client/src/pages/admin/AdminTimelineMana.content:
import React, { useState, useEffect } from 'react';
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import { 
  Calendar, 
  Plus, 
  Edit, 
  Trash2, 
  Save, 
  X, 
  Clock, 
  Star,
  User,
  FileText,
  Search,
  Filter,
  ChevronDown,
  ChevronUp,
  Upload,
  Image as ImageIcon
} from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Textarea } from '@/components/ui/textarea';
import { Badge } from '@/components/ui/badge';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle, DialogTrigger } from '@/components/ui/dialog';
import { Alert, AlertDescription } from '@/components/ui/alert';
import { toast } from '@/hooks/use-toast';
import AdminLayout from '@/components/AdminLayout';

interface TimelineEvent {
  id: string;
  title: string;
  description: string;
  details?: string;
  year: string;
  period: string;
  importance: number;
  keyFigures?: string;
  significance?: string;
  sourceBook?: string;
  chapterReference?: string;
  pageNumber?: number;
  eventImage?: string;
  imageDescription?: string;
  enhanced?: boolean;
  createdAt: string;
  updatedAt: string;
}

// Event Form Component
const EventForm = ({ 
  event, 
  onSubmit, 
  onCancel 
}: { 
  event?: TimelineEvent; 
  onSubmit: (data: Omit<TimelineEvent, 'id' | 'createdAt' | 'updatedAt'>) => void;
  onCancel: () => void;
}) => {
  const [formData, setFormData] = useState({
    title: event?.title || '',
    description: event?.description || '',
    details: event?.details || '',
    year: event?.year || '',
    period: event?.period || '',
    importance: event?.importance || 3,
    keyFigures: event?.keyFigures || '',
    significance: event?.significance || '',
    sourceBook: event?.sourceBook || '',
    chapterReference: event?.chapterReference || '',
    pageNumber: event?.pageNumber || undefined,
    eventImage: event?.eventImage || '',
    imageDescription: event?.imageDescription || ''
  });

  const [isUploading, setIsUploading] = useState(false);
  const [selectedFile, setSelectedFile] = useState<File | null>(null);

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    onSubmit(formData);
  };

  const handleImageUpload = async (file: File) => {
    setIsUploading(true);
    try {
      // Create FormData for file upload
      const formData = new FormData();
      formData.append('image', file);

      // Upload file via server
      const response = await fetch('/api/timeline/upload-image', {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('accessToken')}`
        },
        body: formData
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || 'Failed to upload image');
      }

      const { publicUrl } = await response.json();

      // Update form data with image URL
      setFormData(prev => ({ ...prev, eventImage: publicUrl }));
      toast({
        title: "Success",
        description: "Image uploaded successfully!",
      });
    } catch (error) {
      console.error('Error uploading image:', error);
      toast({
        title: "Error",
        description: error instanceof Error ? error.message : "Failed to upload image. Please try again.",
        variant: "destructive"
      });
    } finally {
      setIsUploading(false);
    }
  };

  const handleFileSelect = (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (file) {
      setSelectedFile(file);
      handleImageUpload(file);
    }
  };

  return (
    <form onSubmit={handleSubmit} className="space-y-3 max-h-[70vh] overflow-y-auto">
      <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
        <div>
          <Label htmlFor="title" className="text-gray-700 dark:text-gray-300">Title *</Label>
          <Input
            id="title"
            value={formData.title}
            onChange={(e: React.ChangeEvent<HTMLInputElement>) => setFormData({ ...formData, title: e.target.value })}
            required
            className="border-gray-300 dark:border-gray-600 bg-white dark:bg-gray-800 text-gray-900 dark:text-white"
          />
        </div>

        <div>
          <Label htmlFor="year" className="text-gray-700 dark:text-gray-300">Year *</Label>
          <Input
            id="year"
            type="number"
            value={formData.year}
            onChange={(e: React.ChangeEvent<HTMLInputElement>) => setFormData({ ...formData, year: e.target.value })}
            required
            className="border-gray-300 dark:border-gray-600 bg-white dark:bg-gray-800 text-gray-900 dark:text-white"
          />
        </div>
      </div>

      <div>
        <Label htmlFor="description" className="text-gray-700 dark:text-gray-300">Description *</Label>
        <Textarea
          id="description"
          value={formData.description}
          onChange={(e: React.ChangeEvent<HTMLTextAreaElement>) => setFormData({ ...formData, description: e.target.value })}
          required
          rows={3}
          className="border-gray-300 dark:border-gray-600 bg-white dark:bg-gray-800 text-gray-900 dark:text-white"
        />
      </div>

      <div>
        <Label htmlFor="details" className="text-gray-700 dark:text-gray-300">Details</Label>
        <Textarea
          id="details"
          value={formData.details}
          onChange={(e: React.ChangeEvent<HTMLTextAreaElement>) => setFormData({ ...formData, details: e.target.value })}
          rows={3}
          className="border-gray-300 dark:border-gray-600 bg-white dark:bg-gray-800 text-gray-900 dark:text-white"
        />
      </div>

      <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
        <div>
          <Label htmlFor="period" className="text-gray-700 dark:text-gray-300">Period *</Label>
          <Select value={formData.period} onValueChange={(value) => setFormData({ ...formData, period: value })}>
            <SelectTrigger className="border-gray-300 dark:border-gray-600 bg-white dark:bg-gray-800 text-gray-900 dark:text-white">
              <SelectValue placeholder="Select period" />
            </SelectTrigger>
            <SelectContent className="bg-white dark:bg-gray-800 border-gray-200 dark:border-gray-700">
              <SelectItem value="medieval" className="text-gray-900 dark:text-white">Medieval</SelectItem>
              <SelectItem value="legal" className="text-gray-900 dark:text-white">Legal</SelectItem>
              <SelectItem value="political" className="text-gray-900 dark:text-white">Political</SelectItem>
              <SelectItem value="royal" className="text-gray-900 dark:text-white">Royal</SelectItem>
            </SelectContent>
          </Select>
        </div>

        <div>
          <Label htmlFor="importance" className="text-gray-700 dark:text-gray-300">Importance *</Label>
          <Select value={formData.importance.toString()} onValueChange={(value) => setFormData({ ...formData, importance: parseInt(value) })}>
            <SelectTrigger className="border-gray-300 dark:border-gray-600 bg-white dark:bg-gray-800 text-gray-900 dark:text-white">
              <SelectValue />
            </SelectTrigger>
            <SelectContent className="bg-white dark:bg-gray-800 border-gray-200 dark:border-gray-700">
              <SelectItem value="1" className="text-gray-900 dark:text-white">1 - Minor</SelectItem>
              <SelectItem value="2" className="text-gray-900 dark:text-white">2 - Somewhat Important</SelectItem>
              <SelectItem value="3" className="text-gray-900 dark:text-white">3 - Important</SelectItem>
              <SelectItem value="4" className="text-gray-900 dark:text-white">4 - Very Important</SelectItem>
              <SelectItem value="5" className="text-gray-900 dark:text-white">5 - Critical</SelectItem>
            </SelectContent>
          </Select>
        </div>

        <div>
          <Label htmlFor="keyFigures" className="text-gray-700 dark:text-gray-300">Key Figures</Label>
          <Input
            id="keyFigures"
            value={formData.keyFigures}
            onChange={(e: React.ChangeEvent<HTMLInputElement>) => setFormData({ ...formData, keyFigures: e.target.value })}
            className="border-gray-300 dark:border-gray-600 bg-white dark:bg-gray-800 text-gray-900 dark:text-white"
          />
        </div>
      </div>

      <div>
        <Label htmlFor="significance" className="text-gray-700 dark:text-gray-300">Significance</Label>
        <Textarea
          id="significance"
          value={formData.significance}
          onChange={(e: React.ChangeEvent<HTMLTextAreaElement>) => setFormData({ ...formData, significance: e.target.value })}
          rows={2}
          className="border-gray-300 dark:border-gray-600 bg-white dark:bg-gray-800 text-gray-900 dark:text-white"
        />
      </div>

      <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
        <div>
          <Label htmlFor="sourceBook" className="text-gray-700 dark:text-gray-300">Source Book</Label>
          <Input
            id="sourceBook"
            value={formData.sourceBook}
            onChange={(e: React.ChangeEvent<HTMLInputElement>) => setFormData({ ...formData, sourceBook: e.target.value })}
            className="border-gray-300 dark:border-gray-600 bg-white dark:bg-gray-800 text-gray-900 dark:text-white"
          />
        </div>

        <div>
          <Label htmlFor="chapterReference" className="text-gray-700 dark:text-gray-300">Chapter Reference</Label>
          <Input
            id="chapterReference"
            value={formData.chapterReference}
            onChange={(e: React.ChangeEvent<HTMLInputElement>) => setFormData({ ...formData, chapterReference: e.target.value })}
            className="border-gray-300 dark:border-gray-600 bg-white dark:bg-gray-800 text-gray-900 dark:text-white"
          />
        </div>

        <div>
          <Label htmlFor="pageNumber" className="text-gray-700 dark:text-gray-300">Page Number</Label>
          <Input
            id="pageNumber"
            type="number"
            value={formData.pageNumber || ''}
            onChange={(e: React.ChangeEvent<HTMLInputElement>) => setFormData({ ...formData, pageNumber: e.target.value ? parseInt(e.target.value) : undefined })}
            className="border-gray-300 dark:border-gray-600 bg-white dark:bg-gray-800 text-gray-900 dark:text-white"
          />
        </div>
      </div>

      {/* Compact Image Upload Section */}
      <div className="space-y-3">
        <div>
          <Label htmlFor="imageDescription" className="text-gray-700 dark:text-gray-300">Image Description</Label>
          <Textarea
            id="imageDescription"
            value={formData.imageDescription}
            onChange={(e: React.ChangeEvent<HTMLTextAreaElement>) => setFormData({ ...formData, imageDescription: e.target.value })}
            rows={2}
            placeholder="Describe the image content..."
            className="border-gray-300 dark:border-gray-600 bg-white dark:bg-gray-700 text-gray-900 dark:text-white"
          />
        </div>

        <div>
          <Label className="text-gray-700 dark:text-gray-300">Event Image</Label>
          <div className="mt-2 space-y-3">
            {/* Compact Image Display */}
            {formData.eventImage && (
              <div className="relative">
                <img 
                  src={formData.eventImage} 
                  alt="Event preview" 
                  className="w-full h-32 object-cover rounded-lg border border-gray-300 dark:border-gray-600"
                />
                <Button
                  type="button"
                  variant="destructive"
                  size="sm"
                  className="absolute top-2 right-2"
                  onClick={() => setFormData({ ...formData, eventImage: '' })}
                >
                  <X className="h-4 w-4" />
                </Button>
              </div>
            )}

            {/* Compact Upload Section */}
            <div className="border-2 border-dashed border-gray-300 dark:border-gray-600 rounded-lg p-4">
              <div className="text-center">
                <ImageIcon className="mx-auto h-8 w-8 text-gray-400" />
                <div className="mt-2">
                  <label htmlFor="image-upload" className="cursor-pointer">
                    <span className="block text-sm font-medium text-gray-900 dark:text-gray-100">
                      {isUploading ? 'Uploading...' : 'Upload an image'}
                    </span>
                    <span className="block text-xs text-gray-500 dark:text-gray-400">
                      PNG, JPG, WEBP up to 10MB
                    </span>
                  </label>
                  <input
                    id="image-upload"
                    type="file"
                    accept="image/*"
                    onChange={handleFileSelect}
                    disabled={isUploading}
                    className="sr-only"
                  />
                </div>
                {!isUploading && (
                  <div className="mt-2">
                    <Button
                      type="button"
                      variant="outline"
                      size="sm"
                      onClick={() => document.getElementById('image-upload')?.click()}
                      className="border-gray-300 dark:border-gray-600"
                    >
                      <Upload className="h-4 w-4 mr-2" />
                      Choose File
                    </Button>
                  </div>
                )}
              </div>
            </div>
          </div>
        </div>
      </div>

      <div className="flex justify-end gap-3 pt-3 border-t border-gray-200 dark:border-gray-700">
        <Button 
          type="button" 
          variant="outline" 
          onClick={onCancel} 
          className="border-gray-300 dark:border-gray-600 text-gray-700 dark:text-gray-300 hover:bg-gray-50 dark:hover:bg-gray-700"
        >
          Cancel
        </Button>
        <Button 
          type="submit" 
          className="bg-blue-600 hover:bg-blue-700 dark:bg-blue-600 dark:hover:bg-blue-700"
        >
          <Save className="h-4 w-4 mr-2" />
          {event ? 'Update Event' : 'Add Event'}
        </Button>
      </div>
    </form>
  );
};

const AdminTimelineManagement = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [filterPeriod, setFilterPeriod] = useState('all');
  const [filterImportance, setFilterImportance] = useState('all');
  const [sortBy, setSortBy] = useState('year');
  const [sortOrder, setSortOrder] = useState<'asc' | 'desc'>('asc');
  const [isEditing, setIsEditing] = useState<string | null>(null);
  const [isAdding, setIsAdding] = useState(false);
  const [expandedEvents, setExpandedEvents] = useState<Set<string>>(new Set());

  const queryClient = useQueryClient();

  // Fetch timeline events
  const { data: events = [], isLoading, error } = useQuery({
    queryKey: ['timeline-events'],
    queryFn: async () => {
      const response = await fetch('/api/timeline');
      if (!response.ok) throw new Error('Failed to fetch timeline events');
      return response.json();
    }
  });

  // Add new event mutation
  const addEventMutation = useMutation({
    mutationFn: async (newEvent: Omit<TimelineEvent, 'id' | 'createdAt' | 'updatedAt'>) => {
      const response = await fetch('/api/timeline', {
        method: 'POST',
        headers: { 
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('accessToken')}`
        },
        body: JSON.stringify(newEvent)
      });
      if (!response.ok) throw new Error('Failed to create event');
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['timeline-events'] });
      toast({ title: 'Success', description: 'Timeline event created successfully' });
      setIsAdding(false);
    },
    onError: (error) => {
      toast({ title: 'Error', description: error.message });
    }
  });

  // Update event mutation
  const updateEventMutation = useMutation({
    mutationFn: async (updatedEvent: { id: string } & Partial<TimelineEvent>) => {
      const response = await fetch(`/api/timeline/${updatedEvent.id}`, {
        method: 'PUT',
        headers: { 
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('accessToken')}`
        },
        body: JSON.stringify(updatedEvent)
      });
      if (!response.ok) throw new Error('Failed to update event');
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['timeline-events'] });
      toast({ title: 'Success', description: 'Timeline event updated successfully' });
      setIsEditing(null);
    },
    onError: (error) => {
      toast({ title: 'Error', description: error.message });
    }
  });

  // Delete event mutation
  const deleteEventMutation = useMutation({
    mutationFn: async (id: string) => {
      const response = await fetch(`/api/timeline/${id}`, {
        method: 'DELETE',
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('accessToken')}`
        }
      });
      if (!response.ok) throw new Error('Failed to delete event');
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['timeline-events'] });
      toast({ title: 'Success', description: 'Timeline event deleted successfully' });
    },
    onError: (error) => {
      toast({ title: 'Error', description: error.message });
    }
  });

  // Filter and sort events
  const filteredEvents = events
    .filter((event: TimelineEvent) => {
      const matchesSearch = event.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
                           event.description.toLowerCase().includes(searchTerm.toLowerCase());
      const matchesPeriod = filterPeriod === 'all' || event.period === filterPeriod;
      const matchesImportance = filterImportance === 'all' || event.importance.toString() === filterImportance;
      return matchesSearch && matchesPeriod && matchesImportance;
    })
    .sort((a: TimelineEvent, b: TimelineEvent) => {
      let comparison = 0;
      switch (sortBy) {
        case 'year':
          comparison = parseInt(a.year) - parseInt(b.year);
          break;
        case 'title':
          comparison = a.title.localeCompare(b.title);
          break;
        case 'importance':
          comparison = a.importance - b.importance;
          break;
        default:
          comparison = 0;
      }
      return sortOrder === 'asc' ? comparison : -comparison;
    });

  const toggleExpanded = (eventId: string) => {
    const newExpanded = new Set(expandedEvents);
    if (newExpanded.has(eventId)) {
      newExpanded.delete(eventId);
    } else {
      newExpanded.add(eventId);
    }
    setExpandedEvents(newExpanded);
  };

  const handleEdit = (event: TimelineEvent) => {
    setIsEditing(event.id);
  };

  const handleDelete = (eventId: string) => {
    if (window.confirm('Are you sure you want to delete this event?')) {
      deleteEventMutation.mutate(eventId);
    }
  };

  const getPeriodColor = (period: string) => {
    const colors: { [key: string]: string } = {
      'medieval': 'bg-blue-100 text-blue-800 dark:bg-blue-900/20 dark:text-blue-300',
      'legal': 'bg-red-100 text-red-800 dark:bg-red-900/20 dark:text-red-300',
      'political': 'bg-purple-100 text-purple-800 dark:bg-purple-900/20 dark:text-purple-300',
      'royal': 'bg-green-100 text-green-800 dark:bg-green-900/20 dark:text-green-300'
    };
    return colors[period] || 'bg-gray-100 text-gray-800 dark:bg-gray-900/20 dark:text-gray-300';
  };

  const getImportanceColor = (importance: number) => {
    const colors: { [key: number]: string } = {
      1: 'bg-gray-100 text-gray-800 dark:bg-gray-900/20 dark:text-gray-300',
      2: 'bg-yellow-100 text-yellow-800 dark:bg-yellow-900/20 dark:text-yellow-300',
      3: 'bg-orange-100 text-orange-800 dark:bg-orange-900/20 dark:text-orange-300',
      4: 'bg-red-100 text-red-800 dark:bg-red-900/20 dark:text-red-300',
      5: 'bg-red-200 text-red-900 dark:bg-red-900/30 dark:text-red-200'
    };
    return colors[importance] || 'bg-gray-100 text-gray-800 dark:bg-gray-900/20 dark:text-gray-300';
  };

  if (isLoading) {
    return (
      <AdminLayout>
        <div className="flex items-center justify-center min-h-[400px]">
          <div className="text-center">
            <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600 mx-auto mb-4"></div>
            <p className="text-gray-600 dark:text-gray-400">Loading timeline events...</p>
          </div>
        </div>
      </AdminLayout>
    );
  }

  if (error) {
    return (
      <AdminLayout>
        <div className="flex items-center justify-center min-h-[400px]">
          <Alert className="max-w-md">
            <AlertDescription>
              Error loading timeline events. Please try again.
            </AlertDescription>
          </Alert>
        </div>
      </AdminLayout>
    );
  }

  return (
    <AdminLayout>
      <div className="container  max-w-7xl h-full flex flex-col">
        {/* Compact Header */}
        <div className="mb-4 flex-shrink-0">
          <div className="flex items-center justify-between">
            <div>
              <h1 className="text-2xl font-bold text-gray-900 dark:text-white">
                Timeline Management
              </h1>
              <p className="text-sm text-gray-600 dark:text-gray-400">
                Manage historical timeline events
              </p>
            </div>
            <Button 
              onClick={() => setIsAdding(true)}
              className="bg-blue-600 hover:bg-blue-700 dark:bg-blue-600 dark:hover:bg-blue-700"
            >
              <Plus className="h-4 w-4 mr-2" />
              Add Event
            </Button>
          </div>
        </div>

        {/* Compact Filters and Search */}
        <Card className="mb-4 bg-white dark:bg-gray-800 border-gray-200 dark:border-gray-700 flex-shrink-0 shadow-lg">
          <CardContent className="p-4">
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
              <div>
                <Label htmlFor="search" className="text-gray-700 dark:text-gray-300">Search</Label>
                <div className="relative">
                  <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-gray-400 dark:text-gray-500" />
                  <Input
                    id="search"
                    placeholder="Search events..."
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                    className="pl-10 border-gray-300 dark:border-gray-600 bg-white dark:bg-gray-800 text-gray-900 dark:text-white"
                  />
                </div>
              </div>

              <div>
                <Label htmlFor="period-filter" className="text-gray-700 dark:text-gray-300">Period</Label>
                <Select value={filterPeriod} onValueChange={setFilterPeriod}>
                  <SelectTrigger className="border-gray-300 dark:border-gray-600 bg-white dark:bg-gray-800 text-gray-900 dark:text-white">
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent className="bg-white dark:bg-gray-800 border-gray-200 dark:border-gray-700">
                    <SelectItem value="all" className="text-gray-900 dark:text-white">All Periods</SelectItem>
                    <SelectItem value="medieval" className="text-gray-900 dark:text-white">Medieval</SelectItem>
                    <SelectItem value="legal" className="text-gray-900 dark:text-white">Legal</SelectItem>
                    <SelectItem value="political" className="text-gray-900 dark:text-white">Political</SelectItem>
                    <SelectItem value="royal" className="text-gray-900 dark:text-white">Royal</SelectItem>
                  </SelectContent>
                </Select>
              </div>

              <div>
                <Label htmlFor="importance-filter" className="text-gray-700 dark:text-gray-300">Importance</Label>
                <Select value={filterImportance} onValueChange={setFilterImportance}>
                  <SelectTrigger className="border-gray-300 dark:border-gray-600 bg-white dark:bg-gray-800 text-gray-900 dark:text-white">
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent className="bg-white dark:bg-gray-800 border-gray-200 dark:border-gray-700">
                    <SelectItem value="all" className="text-gray-900 dark:text-white">All Importance</SelectItem>
                    <SelectItem value="1" className="text-gray-900 dark:text-white">1 - Minor</SelectItem>
                    <SelectItem value="2" className="text-gray-900 dark:text-white">2 - Somewhat Important</SelectItem>
                    <SelectItem value="3" className="text-gray-900 dark:text-white">3 - Important</SelectItem>
                    <SelectItem value="4" className="text-gray-900 dark:text-white">4 - Very Important</SelectItem>
                    <SelectItem value="5" className="text-gray-900 dark:text-white">5 - Critical</SelectItem>
                  </SelectContent>
                </Select>
              </div>

              <div>
                <Label htmlFor="sort" className="text-gray-700 dark:text-gray-300">Sort By</Label>
                <div className="flex gap-2">
                  <Select value={sortBy} onValueChange={setSortBy}>
                    <SelectTrigger className="border-gray-300 dark:border-gray-600 bg-white dark:bg-gray-800 text-gray-900 dark:text-white">
                      <SelectValue />
                    </SelectTrigger>
                    <SelectContent className="bg-white dark:bg-gray-800 border-gray-200 dark:border-gray-700">
                      <SelectItem value="year" className="text-gray-900 dark:text-white">Year</SelectItem>
                      <SelectItem value="title" className="text-gray-900 dark:text-white">Title</SelectItem>
                      <SelectItem value="importance" className="text-gray-900 dark:text-white">Importance</SelectItem>
                    </SelectContent>
                  </Select>
                  <Button
                    variant="outline"
                    size="sm"
                    onClick={() => setSortOrder(sortOrder === 'asc' ? 'desc' : 'asc')}
                    className="border-gray-300 dark:border-gray-600 text-gray-700 dark:text-gray-300 hover:bg-gray-50 dark:hover:bg-gray-700"
                  >
                    {sortOrder === 'asc' ? <ChevronUp className="h-4 w-4" /> : <ChevronDown className="h-4 w-4" />}
                  </Button>
                </div>
              </div>
            </div>
          </CardContent>
        </Card>

        {/* Scrollable Events List */}
        <div className="flex-1 overflow-y-auto">
          <div className="space-y-3">
          {filteredEvents.map((event: TimelineEvent) => (
            <Card key={event.id} className="bg-white dark:bg-gray-800 border-gray-200 dark:border-gray-700 shadow-lg">
              <CardHeader>
                <div className="flex items-start justify-between">
                  <div className="flex-1">
                    <div className="flex items-center gap-3 mb-2">
                      <Badge className={getPeriodColor(event.period)}>
                        {event.period}
                      </Badge>
                      <Badge className={getImportanceColor(event.importance)}>
                        <Star className="h-3 w-3 mr-1" />
                        {event.importance}/5
                      </Badge>
                      {event.enhanced && (
                        <Badge className="bg-green-100 text-green-800 dark:bg-green-900/20 dark:text-green-300">
                          Enhanced
                        </Badge>
                      )}
                    </div>
                    <CardTitle className="text-xl text-gray-900 dark:text-white mb-2">
                      {event.title}
                    </CardTitle>

                    {/* Compact Event Image */}
                    {event.eventImage && (
                      <div className="mb-2">
                        <img 
                          src={event.eventImage} 
                          alt={event.imageDescription || event.title}
                          className="w-full h-32 object-cover rounded-lg border border-gray-200 dark:border-gray-600"
                        />
                        {event.imageDescription && (
                          <p className="text-xs text-gray-600 dark:text-gray-400 mt-1 italic">
                            {event.imageDescription}
                          </p>
                        )}
                      </div>
                    )}

                    <div className="flex items-center gap-4 text-sm text-gray-600 dark:text-gray-400">
                      <div className="flex items-center gap-1">
                        <Clock className="h-4 w-4" />
                        {event.year}
                      </div>
                      {event.keyFigures && (
                        <div className="flex items-center gap-1">
                          <User className="h-4 w-4" />
                          {event.keyFigures}
                        </div>
                      )}
                    </div>
                  </div>
                  <div className="flex items-center gap-2">
                    <Button
                      variant="outline"
                      size="sm"
                      onClick={() => toggleExpanded(event.id)}
                      className="border-gray-300 dark:border-gray-600 text-gray-700 dark:text-gray-300 hover:bg-gray-50 dark:hover:bg-gray-700"
                    >
                      {expandedEvents.has(event.id) ? <ChevronUp className="h-4 w-4" /> : <ChevronDown className="h-4 w-4" />}
                    </Button>
                    <Button
                      variant="outline"
                      size="sm"
                      onClick={() => handleEdit(event)}
                      className="border-gray-300 dark:border-gray-600 text-gray-700 dark:text-gray-300 hover:bg-gray-50 dark:hover:bg-gray-700"
                    >
                      <Edit className="h-4 w-4" />
                    </Button>
                    <Button
                      variant="outline"
                      size="sm"
                      onClick={() => handleDelete(event.id)}
                      className="border-red-300 dark:border-red-600 text-red-600 dark:text-red-400 hover:bg-red-50 dark:hover:bg-red-900/20"
                    >
                      <Trash2 className="h-4 w-4" />
                    </Button>
                  </div>
                </div>
              </CardHeader>

              {expandedEvents.has(event.id) && (
                <CardContent>
                  <div className="space-y-3">
                    <div>
                      <h4 className="font-medium text-gray-900 dark:text-white mb-1">Description</h4>
                      <p className="text-sm text-gray-700 dark:text-gray-300">{event.description}</p>
                    </div>

                    {event.details && (
                      <div>
                        <h4 className="font-medium text-gray-900 dark:text-white mb-1">Details</h4>
                        <p className="text-sm text-gray-700 dark:text-gray-300">{event.details}</p>
                      </div>
                    )}

                    {event.significance && (
                      <div>
                        <h4 className="font-medium text-gray-900 dark:text-white mb-1">Significance</h4>
                        <p className="text-sm text-gray-700 dark:text-gray-300">{event.significance}</p>
                      </div>
                    )}

                    {(event.sourceBook || event.chapterReference || event.pageNumber) && (
                      <div>
                        <h4 className="font-medium text-gray-900 dark:text-white mb-1">Source Information</h4>
                        <div className="text-sm text-gray-700 dark:text-gray-300 space-y-1">
                          {event.sourceBook && <p><strong>Book:</strong> {event.sourceBook}</p>}
                          {event.chapterReference && <p><strong>Chapter:</strong> {event.chapterReference}</p>}
                          {event.pageNumber && <p><strong>Page:</strong> {event.pageNumber}</p>}
                        </div>
                      </div>
                    )}
                  </div>
                </CardContent>
              )}
            </Card>
          ))}

          {filteredEvents.length === 0 && (
            <Card className="bg-white dark:bg-gray-800 border-gray-200 dark:border-gray-700">
              <CardContent className="text-center py-12">
                <Calendar className="h-12 w-12 text-gray-400 dark:text-gray-500 mx-auto mb-4" />
                <h3 className="text-lg font-medium text-gray-900 dark:text-white mb-2">No Events Found</h3>
                <p className="text-gray-600 dark:text-gray-400">
                  {searchTerm || filterPeriod !== 'all' || filterImportance !== 'all' 
                    ? 'Try adjusting your filters to see more events.'
                    : 'Get started by adding your first timeline event.'
                  }
                </p>
              </CardContent>
            </Card>
          )}
          </div>
        </div>

        {/* Add/Edit Event Dialog */}
        <Dialog open={isAdding || !!isEditing} onOpenChange={() => {
          setIsAdding(false);
          setIsEditing(null);
        }}>
          <DialogContent className="max-w-2xl bg-white dark:bg-gray-800 border-gray-200 dark:border-gray-700">
            <DialogHeader>
              <DialogTitle className="text-gray-900 dark:text-white">
                {isAdding ? 'Add New Event' : 'Edit Event'}
              </DialogTitle>
              <DialogDescription className="text-gray-600 dark:text-gray-400">
                {isAdding ? 'Create a new timeline event' : 'Update the timeline event details'}
              </DialogDescription>
            </DialogHeader>

            <EventForm
              event={isEditing ? events.find((e: TimelineEvent) => e.id === isEditing) : undefined}
              onSubmit={(eventData) => {
                if (isEditing) {
                  updateEventMutation.mutate({ id: isEditing, ...eventData });
                } else {
                  addEventMutation.mutate(eventData);
                }
              }}
              onCancel={() => {
                setIsAdding(false);
                setIsEditing(null);
              }}
            />
          </DialogContent>
        </Dialog>
      </div>
    </AdminLayout>
  );
};

export default AdminTimelineManagement;

