export default function TimelinePage() {
  const [selectedTopic, setSelectedTopic] = useState<string | null>(null);
  const [, setLocation] = useLocation();


  const { data: allEvents, isLoading } = useQuery<TimelineEvent[]>({
    queryKey: ["/api/timeline"],
    queryFn: () => fetch('/api/timeline').then(res => res.json()),
  });

  // Filter events by selected topic
  const events = allEvents?.filter(event => 
    (event.timelineTopic || event.significance) === selectedTopic
  ) || [];

  const selectedTopicData = TIMELINE_TOPICS.find(t => t.id === selectedTopic);


  const getImportanceColor = (importance: number) => {
    switch (importance) {
      case 5: return "bg-red-100 text-red-800 border-red-200";
      case 4: return "bg-orange-100 text-orange-800 border-orange-200";
      case 3: return "bg-yellow-100 text-yellow-800 border-yellow-200";
      case 2: return "bg-blue-100 text-blue-800 border-blue-200";
      case 1: return "bg-gray-100 text-gray-800 border-gray-200";
      default: return "bg-gray-100 text-gray-800 border-gray-200";
    }
  };

  const getImportanceStars = (importance: number) => {
    return Array.from({ length: 5 }, (_, i) => (
      <Star 
        key={i} 
        className={`h-3 w-3 ${i < importance ? 'text-yellow-500 fill-yellow-500' : 'text-gray-300'}`} 
      />
    ));
  };


  if (!selectedTopic) {
    return (
      <div className="min-h-screen bg-gray-50 dark:bg-gray-900">
        {/* Header */}
        <div className="bg-white dark:bg-gray-800 shadow-sm border-b border-gray-200 dark:border-gray-700">
          <div className="container mx-auto max-w-6xl px-4 py-4 sm:py-6">
            <div className="flex flex-col space-y-4 sm:flex-row sm:items-center sm:justify-between sm:space-y-0">
              <div className="flex-1">
                <h1 className="text-3xl sm:text-3xl font-bold text-gray-900 dark:text-white">Interactive Historical Timeline</h1>
                <p className="text-base sm:text-base text-gray-600 dark:text-gray-400 mt-1 sm:mt-2">Explore key moments in British history by topic</p>
              </div>
              <Button 
                variant="outline" 
                onClick={() => setLocation("/dashboard")}
                className="w-full sm:w-auto border-gray-200 dark:border-gray-700 text-gray-700 dark:text-gray-300 hover:bg-gray-50 dark:hover:bg-gray-700"
              >
                <ArrowLeft className="h-4 w-4 mr-2" />
                Back to Dashboard
              </Button>
            </div>
          </div>
        </div>

        {/* Topic Selection */}
        <div className="container mx-auto max-w-6xl p-4">
          <div className="mb-6 sm:mb-8">
            <div className="bg-blue-50 dark:bg-blue-900/20 border border-blue-200 dark:border-blue-800 rounded-lg p-3 sm:p-4">
              <div className="flex flex-col sm:flex-row sm:items-start">
                <Calendar className="h-4 w-4 sm:h-5 sm:w-5 text-blue-800 dark:text-blue-200 mr-2 mt-0.5 flex-shrink-0 font-bold" />
                <div className="flex-1">
                <h3 className="font-medium text-blue-900 dark:text-blue-100 mb-1 text-base sm:text-base">Choose Your Historical Journey</h3>
                <p className="text-blue-700 dark:text-blue-300 text-sm sm:text-sm">
                  Select a timeline topic to explore major events, key figures, and important developments
                  that shaped the United Kingdom throughout history.
                </p>
                </div>
                <div className="flex items-center text-xs text-blue-600 dark:text-blue-400 bg-blue-100 dark:bg-blue-800 px-2 py-1 rounded-full mt-2 sm:mt-0 sm:ml-4">
                  🎵 Narration Available
                </div>
              </div>
            </div>
          </div>

          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-4 sm:gap-6">
            {TIMELINE_TOPICS.map((topic) => {
              const IconComponent = topic.icon;
              return (
                <Card 
                  key={topic.id} 
                  className="hover:shadow-lg transition-all duration-200 cursor-pointer border-2 border-gray-200 dark:border-gray-700 hover:border-blue-300 dark:hover:border-blue-600 group bg-white dark:bg-gray-800"
                  onClick={() => setSelectedTopic(topic.id)}
                >
                  <CardHeader className="pb-3">
                    <div className={`w-10 h-10 sm:w-12 sm:h-12 rounded-lg flex items-center justify-center mb-3 group-hover:scale-110 transition-transform`}>
                      <IconComponent className="h-5 w-5 sm:h-6 sm:w-6 text-white drop-shadow-lg font-bold" />
                    </div>
                    <CardTitle className="text-lg sm:text-lg group-hover:text-blue-600 dark:group-hover:text-blue-400 transition-colors text-gray-900 dark:text-white line-clamp-2">
                      {topic.title}
                    </CardTitle>
                    <CardDescription className="text-sm sm:text-sm leading-relaxed text-gray-600 dark:text-gray-400 line-clamp-3">
                      {topic.description}
                    </CardDescription>
                  </CardHeader>
                  <CardContent className="pt-0">
                    <div className="space-y-2">
                      <Button 
                        className="w-full group-hover:bg-blue-600 group-hover:text-white transition-colors border-gray-200 dark:border-gray-700 text-gray-700 dark:text-gray-300 text-sm sm:text-sm" 
                        variant="outline"
                      >
                        Explore Timeline
                      </Button>
                      <Button 
                        className="w-full bg-green-100 dark:bg-green-900/20 text-green-800 dark:text-green-400 hover:bg-green-200 dark:hover:bg-green-900/30 border-green-200 dark:border-green-800 text-sm sm:text-sm" 
                        variant="outline"
                        onClick={(e) => {
                          e.stopPropagation();
                          setLocation(`/timeline-test/${topic.id}`);
                        }}
                      >
                        Test
                      </Button>
                    </div>
                  </CardContent>
                </Card>
              );
            })}
          </div>
        </div>
      </div>
    );
  }

  return (
    <TTSProvider>
      <div className="min-h-screen bg-gray-50 dark:bg-gray-900">
      {/* Header */}
      <div className="bg-white dark:bg-gray-800 shadow-sm border-b border-gray-200 dark:border-gray-700">
        <div className="container mx-auto max-w-6xl px-4 py-4 sm:py-6">
          <div className="flex flex-col space-y-4 sm:flex-row sm:items-center sm:justify-between sm:space-y-0">
            <div className="flex flex-col space-y-3 sm:flex-row sm:items-center sm:space-y-0 sm:space-x-4">
              <Button 
                variant="outline" 
                size="sm"
                onClick={() => setSelectedTopic(null)}
                className="w-full sm:w-auto border-gray-200 dark:border-gray-700 text-gray-700 dark:text-gray-300 hover:bg-gray-50 dark:hover:bg-gray-700"
              >
                <ArrowLeft className="h-4 w-4 mr-2" />
                Back to Topics
              </Button>
              <div className="min-w-0 flex-1">
                <h1 className="text-xl sm:text-2xl font-bold text-gray-900 dark:text-white truncate">{selectedTopicData?.title}</h1>
                <p className="text-sm sm:text-sm text-gray-600 dark:text-gray-400 truncate">{selectedTopicData?.description}</p>
              </div>
            </div>
            <div className="flex items-center justify-end sm:justify-start">
              <Badge className={`${selectedTopicData?.color} text-xs sm:text-sm`}>
                {events?.length || 0} Events
              </Badge>
            </div>
          </div>
        </div>
      </div>

      {/* Timeline Content */}
      <div className="container mx-auto max-w-7xl p-4">
        {/* Learning Modules Grid */}
        <div className="grid grid-cols-1 lg:grid-cols-3 gap-6 lg:gap-8">

          {/* Left Column - Main Content */}
          <div className="lg:col-span-2 space-y-6 lg:space-y-8">
            {isLoading ? (
              <div className="text-center py-8 sm:py-12">
                <div className="animate-spin rounded-full h-8 w-8 sm:h-12 sm:w-12 border-b-2 border-blue-600 dark:border-blue-400 mx-auto"></div>
                <p className="mt-4 text-base sm:text-base text-gray-600 dark:text-gray-400">Loading timeline events...</p>
              </div>
            ) : (
              <div className="relative max-w-6xl mx-auto">
                {/* Central Timeline Line */}
                <div className="absolute left-1/2 top-0 bottom-0 w-1 bg-gradient-to-b from-blue-600 via-purple-600 to-red-600 transform -translate-x-1/2 shadow-sm"></div>

                <div className="space-y-8 sm:space-y-12">
                  {events?.sort((a, b) => a.year - b.year).map((event, index) => {
                    const isEven = index % 2 === 0;
                    return (
                      <div key={event.id} className="relative">
                        {/* Timeline Dot */}
                        <div className={`absolute left-1/2 w-12 h-12 sm:w-16 sm:h-16 ${selectedTopicData?.bgColor || 'bg-gradient-to-br from-blue-500 to-purple-600'} rounded-full border-4 border-white dark:border-gray-800 shadow-lg flex items-center justify-center transform -translate-x-1/2 z-10`}>
                          <span className="font-bold text-xs sm:text-sm text-white">{event.year}</span>
                        </div>

                        {/* Event Content */}
                        <div className={`flex flex-col lg:flex-row lg:items-center ${isEven ? 'lg:flex-row' : 'lg:flex-row-reverse'} mt-6 sm:mt-8`}>
                          {/* Event Card */}
                          <div className={`w-full lg:w-5/12 ${isEven ? 'lg:pr-8' : 'lg:pl-8'}`}>
                            <Card className="shadow-md hover:shadow-lg transition-shadow bg-white dark:bg-gray-800 border-gray-200 dark:border-gray-700">
                              <CardHeader className="pb-3">
                                <div className="flex flex-col space-y-3 sm:flex-row sm:items-start sm:justify-between sm:space-y-0">
                                  <div className="flex-1 min-w-0">
                                    <div className="flex flex-col space-y-2 sm:flex-row sm:items-center sm:space-y-0 sm:space-x-2 mb-2">
                                      <Badge className={`${getImportanceColor(event.importance)} text-xs w-fit`}>
                                        Level {event.importance}
                                      </Badge>
                                      <div className="flex items-center space-x-1">
                                        {getImportanceStars(event.importance)}
                                      </div>
                                    </div>
                                    <CardTitle className="text-lg sm:text-lg text-gray-900 dark:text-white line-clamp-2">{event.title}</CardTitle>
                                    <div className="flex flex-col space-y-1 sm:flex-row sm:items-center sm:space-y-0 sm:space-x-2 text-sm sm:text-sm text-gray-500 dark:text-gray-400 mt-1">
                                      <div className="flex items-center">
                                        <Clock className="h-3 w-3 sm:h-4 sm:w-4 mr-1" />
                                        <span>{event.year}</span>
                                      </div>
                                      {event.keyFigures && (
                                        <div className="flex items-center">
                                          <span className="hidden sm:inline mx-2">•</span>
                                          <Users className="h-3 w-3 sm:h-4 sm:w-4 mr-1" />
                                          <span>Key Figures</span>
                                        </div>
                                      )}
                                    </div>
                                  </div>

                                </div>
                              </CardHeader>
                              <CardContent className="pt-0">
                                <p className="text-base sm:text-base text-gray-700 dark:text-gray-300 mb-3">{event.description}</p>

                                {event.keyFigures && (
                                  <div className="mb-3">
                                    <p className="text-sm sm:text-sm font-medium text-gray-900 dark:text-white mb-1">Key Figures:</p>
                                    <p className="text-sm sm:text-sm text-gray-600 dark:text-gray-400">{event.keyFigures}</p>
                                  </div>
                                )}

                                {event.details && (
                                  <div className="bg-gray-50 dark:bg-gray-700 rounded-lg p-2 sm:p-3">
                                    <p className="text-sm sm:text-sm text-gray-700 dark:text-gray-300">{event.details}</p>
                                  </div>
                                )}

                                {/* Enhanced AI Voice Narration */}
                                <div className="mt-4">
                                  <NarrateButton
                                    eventId={event.id}
                                    title={event.title}
                                    description={event.description}
                                    details={event.details}
                                    variant="outline"
                                    size="sm"
                                    className="w-full"
                                    allEvents={allEvents}
                                  />
                                </div>

                                {selectedTopic === 'british_sportsmen' && (
                                  <div className="mt-3 sm:mt-4 p-3 sm:p-4 bg-emerald-50 dark:bg-emerald-900/20 border border-emerald-200 dark:border-emerald-800 rounded-lg">
                                    <div className="flex flex-col space-y-3 sm:flex-row sm:items-start sm:justify-between sm:space-y-0 sm:gap-4">
                                      <div className="flex-1 min-w-0">
                                        {event.sport && (
                                          <div className="mb-2">
                                            <Badge className="bg-emerald-100 dark:bg-emerald-800 text-emerald-800 dark:text-emerald-200 text-xs">
                                              {event.sport}
                                            </Badge>
                                          </div>
                                        )}
                                        {event.accomplishment && (
                                          <div className="mb-3">
                                            <h5 className="font-medium text-emerald-900 dark:text-emerald-100 mb-1 text-sm sm:text-sm">Achievement:</h5>
                                            <p className="text-sm sm:text-sm text-emerald-800 dark:text-emerald-200">{event.accomplishment}</p>
                                          </div>
                                        )}
                                        <div className="flex items-center gap-2">
                                          <Video className="h-3 w-3 sm:h-4 sm:w-4 text-emerald-600 dark:text-emerald-400" />
                                          <span className="text-sm sm:text-sm font-medium text-emerald-900 dark:text-emerald-100">Video Link:</span>
                                        </div>
                                      </div>
                                      <div className="flex-shrink-0">
                                        {event.videoLink ? (
                                          <Button
                                            size="sm"
                                            className="bg-emerald-600 hover:bg-emerald-700 text-white text-sm sm:text-sm"
                                            onClick={() => window.open(event.videoLink, '_blank')}
                                          >
                                            <Play className="h-3 w-3 mr-1" />
                                            Watch
                                            <ExternalLink className="h-3 w-3 ml-1" />
                                          </Button>
                                        ) : (
                                          <div className="p-2 border-2 border-dashed border-emerald-300 rounded-lg text-center min-w-[80px] sm:min-w-[100px]">
                                            <Video className="h-3 w-3 sm:h-4 sm:w-4 text-emerald-400 mx-auto mb-1" />
                                            <p className="text-sm text-emerald-600">Video link space</p>
                                            <p className="text-sm text-emerald-500">To be added</p>
                                          </div>
                                        )}
                                      </div>
                                    </div>
                                  </div>
                                )}
                              </CardContent>
                            </Card>
                          </div>
                        </div>
                      </div>
                    );
                  })}
                </div>

                {events && events.length === 0 && (
                  <div className="text-center py-8 sm:py-12">
                    <Calendar className="h-10 w-10 sm:h-12 sm:w-12 text-gray-700 dark:text-gray-300 mx-auto mb-3 sm:mb-4 font-bold" />
                    <h3 className="text-lg sm:text-lg font-medium text-gray-900 dark:text-white mb-2">No Events Found</h3>
                    <p className="text-base sm:text-base text-gray-600 dark:text-gray-400">This timeline topic is being prepared. Please check back soon.</p>
                  </div>
                )}
              </div>
            )}
          </div>

          {/* Right Column - Sidebar */}
          <div className="space-y-3 lg:space-y-4">
            {/* Fixed TTS Narration Group */}
            <div className="sticky top-20 space-y-6 lg:space-y-4 z-10">
              <GlobalTTSNarration />
            </div>
          </div>
        </div>
      </div>


      <MobileNav />
      <FloatingChatbot />

      {/* Add padding bottom for mobile navigation */}
      <div className="h-16 sm:h-20 md:hidden"></div>
      </div>
    </TTSProvider>
  );
}

client/src/components/admin/BulkTestOper.content:
import React, { useState } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Switch } from '@/components/ui/switch';
import { Badge } from '@/components/ui/badge';
import { Alert, AlertDescription } from '@/components/ui/alert';
import { Checkbox } from '@/components/ui/checkbox';
import { 
  Copy, 
  Archive, 
  Download, 
  Upload, 
  Trash2, 
  Edit, 
  Settings,
  CheckCircle,
  AlertTriangle,
  X,
  Plus,
  Minus
} from 'lucide-react';

interface Test {
  id: string;
  title: string;
  category: string;
  difficulty: number;
  isActive: boolean;
  questions: any[];
}

interface BulkTestOperationsProps {
  selectedTests: string[];
  tests: Test[];
  onSelectionChange: (selectedIds: string[]) => void;
  onBulkOperation: (operation: string, testIds: string[], options?: any) => Promise<void>;
  testType: 'practice' | 'mock';
}

const BulkTestOperations: React.FC<BulkTestOperationsProps> = ({
  selectedTests,
  tests,
  onSelectionChange,
  onBulkOperation,
  testType
}) => {
  const [showOperations, setShowOperations] = useState(false);
  const [selectedOperation, setSelectedOperation] = useState('');
  const [operationOptions, setOperationOptions] = useState<any>({});
  const [loading, setLoading] = useState(false);
  const [selectAll, setSelectAll] = useState(false);

  const operations = [
    {
      id: 'duplicate',
      label: 'Duplicate Tests',
      description: 'Create copies of selected tests',
      icon: Copy,
      color: 'bg-blue-100 text-blue-800'
    },
    {
      id: 'archive',
      label: 'Archive Tests',
      description: 'Move tests to archive (inactive)',
      icon: Archive,
      color: 'bg-yellow-100 text-yellow-800'
    },
    {
      id: 'activate',
      label: 'Activate Tests',
      description: 'Make tests available to students',
      icon: CheckCircle,
      color: 'bg-green-100 text-green-800'
    },
    {
      id: 'deactivate',
      label: 'Deactivate Tests',
      description: 'Hide tests from students',
      icon: X,
      color: 'bg-red-100 text-red-800'
    },
    {
      id: 'update_category',
      label: 'Update Category',
      description: 'Change category for selected tests',
      icon: Edit,
      color: 'bg-purple-100 text-purple-800'
    },
    {
      id: 'update_difficulty',
      label: 'Update Difficulty',
      description: 'Change difficulty level',
      icon: Settings,
      color: 'bg-orange-100 text-orange-800'
    },
    {
      id: 'export',
      label: 'Export Tests',
      description: 'Download test data',
      icon: Download,
      color: 'bg-indigo-100 text-indigo-800'
    },
    {
      id: 'delete',
      label: 'Delete Tests',
      description: 'Permanently remove tests',
      icon: Trash2,
      color: 'bg-red-100 text-red-800'
    }
  ];

  const categories = [
    'British History',
    'Government & Politics',
    'Geography & Culture',
    'Laws & Society'
  ];

  const difficulties = [
    { value: 1, label: 'Beginner' },
    { value: 2, label: 'Easy' },
    { value: 3, label: 'Medium' },
    { value: 4, label: 'Hard' },
    { value: 5, label: 'Expert' }
  ];

  const handleSelectAll = (checked: boolean) => {
    setSelectAll(checked);
    if (checked) {
      onSelectionChange(tests.map(test => test.id));
    } else {
      onSelectionChange([]);
    }
  };

  const handleTestSelection = (testId: string, checked: boolean) => {
    if (checked) {
      onSelectionChange([...selectedTests, testId]);
    } else {
      onSelectionChange(selectedTests.filter(id => id !== testId));
    }
  };

  const handleOperationSelect = (operationId: string) => {
    setSelectedOperation(operationId);
    setOperationOptions({});

    // Set default options based on operation
    switch (operationId) {
      case 'duplicate':
        setOperationOptions({ addSuffix: true, suffix: ' (Copy)' });
        break;
      case 'update_category':
        setOperationOptions({ category: 'British History' });
        break;
      case 'update_difficulty':
        setOperationOptions({ difficulty: 3 });
        break;
      case 'export':
        setOperationOptions({ format: 'json' });
        break;
      default:
        setOperationOptions({});
    }
  };

  const handleExecuteOperation = async () => {
    if (selectedTests.length === 0) {
      alert('Please select at least one test');
      return;
    }

    if (!selectedOperation) {
      alert('Please select an operation');
      return;
    }

    setLoading(true);
    try {
      await onBulkOperation(selectedOperation, selectedTests, operationOptions);
      setShowOperations(false);
      setSelectedOperation('');
      setOperationOptions({});
    } catch (error) {
      console.error('Bulk operation failed:', error);
      alert('Operation failed. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const getOperationIcon = (operationId: string) => {
    const operation = operations.find(op => op.id === operationId);
    return operation ? operation.icon : Settings;
  };

  const getOperationColor = (operationId: string) => {
    const operation = operations.find(op => op.id === operationId);
    return operation ? operation.color : 'bg-gray-100 text-gray-800';
  };

  return (
    <div className="space-y-4">
      {/* Selection Controls */}
      <Card>
        <CardContent className="p-4">
          <div className="flex items-center justify-between">
            <div className="flex items-center gap-4">
              <div className="flex items-center space-x-2">
                <Checkbox
                  id="select-all"
                  checked={selectAll}
                  onCheckedChange={handleSelectAll}
                />
                <Label htmlFor="select-all">Select All</Label>
              </div>
              <span className="text-sm text-gray-600">
                {selectedTests.length} of {tests.length} tests selected
              </span>
            </div>
            <div className="flex gap-2">
              <Button
                variant="outline"
                onClick={() => setShowOperations(!showOperations)}
                disabled={selectedTests.length === 0}
              >
                <Settings className="w-4 h-4 mr-2" />
                Bulk Operations
              </Button>
              <Button
                variant="outline"
                onClick={() => onSelectionChange([])}
                disabled={selectedTests.length === 0}
              >
                Clear Selection
              </Button>
            </div>
          </div>
        </CardContent>
      </Card>

      {/* Test List with Selection */}
      <div className="space-y-2">
        {tests.map((test) => (
          <Card key={test.id} className="hover:bg-gray-50">
            <CardContent className="p-4">
              <div className="flex items-center justify-between">
                <div className="flex items-center gap-3">
                  <Checkbox
                    checked={selectedTests.includes(test.id)}
                    onCheckedChange={(checked) => handleTestSelection(test.id, checked as boolean)}
                  />
                  <div className="flex-1">
                    <div className="flex items-center gap-2 mb-1">
                      <h3 className="font-medium">{test.title}</h3>
                      <Badge variant={test.isActive ? 'default' : 'secondary'}>
                        {test.isActive ? 'Active' : 'Inactive'}
                      </Badge>
                    </div>
                    <div className="flex items-center gap-4 text-sm text-gray-600">
                      <span>Category: {test.category}</span>
                      <span>Difficulty: {test.difficulty}</span>
                      <span>Questions: {test.questions?.length || 0}</span>
                    </div>
                  </div>
                </div>
              </div>
            </CardContent>
          </Card>
        ))}
      </div>

      {/* Bulk Operations Panel */}
      {showOperations && (
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <Settings className="w-5 h-5" />
              Bulk Operations
            </CardTitle>
          </CardHeader>
          <CardContent className="space-y-4">
            {/* Operation Selection */}
            <div>
              <Label>Select Operation</Label>
              <div className="grid grid-cols-2 md:grid-cols-4 gap-2 mt-2">
                {operations.map((operation) => {
                  const IconComponent = operation.icon;
                  return (
                    <Button
                      key={operation.id}
                      variant={selectedOperation === operation.id ? 'default' : 'outline'}
                      onClick={() => handleOperationSelect(operation.id)}
                      className="h-auto p-3 flex flex-col items-center gap-2"
                    >
                      <IconComponent className="w-4 h-4" />
                      <span className="text-xs text-center">{operation.label}</span>
                    </Button>
                  );
                })}
              </div>
            </div>

            {/* Operation Options */}
            {selectedOperation && (
              <div className="space-y-4">
                <div className="p-3 bg-gray-50 rounded-lg">
                  <h4 className="font-medium mb-2">
                    {operations.find(op => op.id === selectedOperation)?.label}
                  </h4>
                  <p className="text-sm text-gray-600">
                    {operations.find(op => op.id === selectedOperation)?.description}
                  </p>
                </div>

                {/* Duplicate Options */}
                {selectedOperation === 'duplicate' && (
                  <div className="space-y-3">
                    <div className="flex items-center space-x-2">
                      <Switch
                        id="add-suffix"
                        checked={operationOptions.addSuffix}
                        onCheckedChange={(checked) => 
                          setOperationOptions({...operationOptions, addSuffix: checked})
                        }
                      />
                      <Label htmlFor="add-suffix">Add suffix to titles</Label>
                    </div>
                    {operationOptions.addSuffix && (
                      <div>
                        <Label htmlFor="suffix">Suffix</Label>
                        <Input
                          id="suffix"
                          value={operationOptions.suffix || ''}
                          onChange={(e) => 
                            setOperationOptions({...operationOptions, suffix: e.target.value})
                          }
                          placeholder="e.g., (Copy)"
                        />
                      </div>
                    )}
                  </div>
                )}

                {/* Category Update Options */}
                {selectedOperation === 'update_category' && (
                  <div>
                    <Label htmlFor="new-category">New Category</Label>
                    <Select 
                      value={operationOptions.category || ''} 
                      onValueChange={(value) => 
                        setOperationOptions({...operationOptions, category: value})
                      }
                    >
                      <SelectTrigger>
                        <SelectValue placeholder="Select category" />
                      </SelectTrigger>
                      <SelectContent>
                        {categories.map(category => (
                          <SelectItem key={category} value={category}>{category}</SelectItem>
                        ))}
                      </SelectContent>
                    </Select>
                  </div>
                )}

                {/* Difficulty Update Options */}
                {selectedOperation === 'update_difficulty' && (
                  <div>
                    <Label htmlFor="new-difficulty">New Difficulty</Label>
                    <Select 
                      value={operationOptions.difficulty?.toString() || ''} 
                      onValueChange={(value) => 
                        setOperationOptions({...operationOptions, difficulty: parseInt(value)})
                      }
                    >
                      <SelectTrigger>
                        <SelectValue placeholder="Select difficulty" />
                      </SelectTrigger>
                      <SelectContent>
                        {difficulties.map(difficulty => (
                          <SelectItem key={difficulty.value} value={difficulty.value.toString()}>
                            {difficulty.label}
                          </SelectItem>
                        ))}
                      </SelectContent>
                    </Select>
                  </div>
                )}

                {/* Export Options */}
                {selectedOperation === 'export' && (
                  <div>
                    <Label htmlFor="export-format">Export Format</Label>
                    <Select 
                      value={operationOptions.format || 'json'} 
                      onValueChange={(value) => 
                        setOperationOptions({...operationOptions, format: value})
                      }
                    >
                      <SelectTrigger>
                        <SelectValue />
                      </SelectTrigger>
                      <SelectContent>
                        <SelectItem value="json">JSON</SelectItem>
                        <SelectItem value="csv">CSV</SelectItem>
                        <SelectItem value="pdf">PDF</SelectItem>
                      </SelectContent>
                    </Select>
                  </div>
                )}

                {/* Confirmation for Destructive Operations */}
                {(selectedOperation === 'delete' || selectedOperation === 'deactivate') && (
                  <Alert>
                    <AlertTriangle className="h-4 w-4" />
                    <AlertDescription>
                      This operation will {selectedOperation === 'delete' ? 'permanently delete' : 'deactivate'} {selectedTests.length} test(s). 
                      This action cannot be undone.
                    </AlertDescription>
                  </Alert>
                )}

                {/* Execute Button */}
                <div className="flex justify-end gap-2">
                  <Button variant="outline" onClick={() => setShowOperations(false)}>
                    Cancel
                  </Button>
                  <Button 
                    onClick={handleExecuteOperation}
                    disabled={loading}
                    className={getOperationColor(selectedOperation)}
                  >
                    {loading ? (
                      <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2"></div>
                    ) : (
                      React.createElement(getOperationIcon(selectedOperation), { className: "w-4 h-4 mr-2" })
                    )}
                    Execute {operations.find(op => op.id === selectedOperation)?.label}
                  </Button>
                </div>
              </div>
            )}
          </CardContent>
        </Card>
      )}
    </div>
  );
};

export default BulkTestOperations;

client/src/pages/admin/AdminAnalytics.ts.content:
import { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Progress } from "@/components/ui/progress";
import { useToast } from "@/hooks/use-toast";
import AdminLayout from "@/components/AdminLayout";
import { 
  LineChart, 
  Line, 
  AreaChart, 
  Area, 
  BarChart, 
  Bar, 
  PieChart, 
  Pie, 
  Cell, 
  XAxis, 
  YAxis, 
  CartesianGrid, 
  Tooltip, 
  Legend, 
  ResponsiveContainer 
} from "recharts";
import { 
  BarChart3, 
  TrendingUp, 
  TrendingDown,
  Users, 
  UserCheck, 
  Crown, 
  DollarSign,
  Target,
  Award,
  BookOpen,
  Video,
  FileText,
  Clock,
  Calendar,
  Download,
  RefreshCw,
  Eye,
  MousePointer,
  Heart,
  Share,
  MessageCircle,
  Star,
  Zap,
  Globe,
  Shield,
  Activity,
  TrendingUp as TrendingUpIcon,
  TrendingDown as TrendingDownIcon,
  ArrowUp,
  ArrowDown,
  Minus
} from "lucide-react";

interface AnalyticsData {
  overview: {
    totalUsers: number;
    activeUsers: number;
    newUsers: number;
    revenue: number;
    conversionRate: number;
    averageSessionTime: number;
    bounceRate: number;
  };
  userGrowth: {
    daily: Array<{ date: string; users: number }>;
    weekly: Array<{ week: string; users: number }>;
    monthly: Array<{ month: string; users: number }>;
  };
  contentPerformance: {
    videos: Array<{ title: string; views: number; completionRate: number }>;
    exercises: Array<{ title: string; attempts: number; successRate: number }>;
    tests: Array<{ title: string; completions: number; averageScore: number }>;
  };
  revenue: {
    monthly: number;
    yearly: number;
    growth: number;
    subscriptionBreakdown: {
      premium: number;
      free: number;
    };
  };
  engagement: {
    averageSessionTime: number;
    pageViews: number;
    uniqueVisitors: number;
    returnVisitors: number;
  };
}

export default function AdminAnalytics() {
  const [timeRange, setTimeRange] = useState("30d");
  const [activeTab, setActiveTab] = useState("overview");

  const { toast } = useToast();

  const { data: analytics, isLoading, refetch, error } = useQuery<AnalyticsData>({
    queryKey: ["/api/admin/analytics", timeRange],
    queryFn: async () => {
      const response = await fetch(`/api/admin/analytics?timeRange=${timeRange}`, {
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('accessToken')}`,
          'Content-Type': 'application/json'
        }
      });
      if (!response.ok) {
        throw new Error('Failed to fetch analytics data');
      }
      return response.json();
    },
    refetchInterval: 30000, // Refetch every 30 seconds
  });

  const handleRefresh = () => {
    refetch();
    toast({
      title: "Analytics Refreshed",
      description: "Analytics data has been updated successfully.",
    });
  };

  const handleExport = async () => {
    try {
      const response = await fetch(`/api/admin/analytics/export?timeRange=${timeRange}`, {
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('accessToken')}`,
          'Content-Type': 'application/json'
        }
      });

      if (!response.ok) {
        throw new Error('Failed to export analytics data');
      }

      const blob = await response.blob();
      const url = window.URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = `analytics-${new Date().toISOString().split('T')[0]}.csv`;
      document.body.appendChild(a);
      a.click();
      window.URL.revokeObjectURL(url);
      document.body.removeChild(a);

      toast({
        title: "Export Successful",
        description: "Analytics data has been exported successfully.",
      });
    } catch (error) {
      toast({
        title: "Export Failed",
        description: "Failed to export analytics data. Please try again.",
        variant: "destructive",
      });
    }
  };

  const formatCurrency = (amount: number) => {
    return new Intl.NumberFormat('en-GB', {
      style: 'currency',
      currency: 'GBP'
    }).format(amount);
  };

  const formatNumber = (num: number) => {
    return new Intl.NumberFormat('en-GB').format(num);
  };

  const formatPercentage = (num: number) => {
    return `${num.toFixed(1)}%`;
  };

  const formatTime = (minutes: number) => {
    const hours = Math.floor(minutes / 60);
    const mins = Math.floor(minutes % 60);
    return `${hours}h ${mins}m`;
  };

  const getTrendIcon = (trend: number) => {
    if (trend > 0) return <TrendingUp className="h-4 w-4 text-green-500" />;
    if (trend < 0) return <TrendingDown className="h-4 w-4 text-red-500" />;
    return <Minus className="h-4 w-4 text-gray-500" />;
  };

  const getTrendColor = (trend: number) => {
    if (trend > 0) return "text-green-600";
    if (trend < 0) return "text-red-600";
    return "text-gray-600";
  };

  // Only use real data from backend
  const displayData = analytics;

  if (isLoading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="text-center">
          <div className="w-8 h-8 border-2 border-blue-600 border-t-transparent rounded-full animate-spin mx-auto mb-4"></div>
          <p className="text-gray-600 dark:text-gray-400">Loading analytics...</p>
        </div>
      </div>
    );
  }

  if (!displayData) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="text-center">
          <div className="w-12 h-12 text-gray-400 dark:text-gray-500 mx-auto mb-4">
            <svg fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 19v-6a2 2 0 00-2-2H5a2 2 0 00-2 2v6a2 2 0 002 2h2a2 2 0 002-2zm0 0V9a2 2 0 012-2h2a2 2 0 012 2v10m-6 0a2 2 0 002 2h2a2 2 0 002-2m0 0V5a2 2 0 012-2h2a2 2 0 012 2v14a2 2 0 01-2 2h-2a2 2 0 01-2-2z" />
            </svg>
          </div>
          <p className="text-gray-600 dark:text-gray-400">No analytics data available</p>
          <p className="text-sm text-gray-500 dark:text-gray-500 mt-2">Please ensure you're logged in as an admin</p>
        </div>
      </div>
    );
  }

  return (
    <AdminLayout>
      <div className="space-y-6">
      {/* Error Notice */}
      {error && (
        <div className="bg-red-50 dark:bg-red-900/20 border border-red-200 dark:border-red-800 rounded-lg p-4">
          <div className="flex items-center">
            <div className="flex-shrink-0">
              <svg className="h-5 w-5 text-red-400" viewBox="0 0 20 20" fill="currentColor">
                <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zM8.707 7.293a1 1 0 00-1.414 1.414L8.586 10l-1.293 1.293a1 1 0 101.414 1.414L10 11.414l1.293 1.293a1 1 0 001.414-1.414L11.414 10l1.293-1.293a1 1 0 00-1.414-1.414L10 8.586 8.707 7.293z" clipRule="evenodd" />
              </svg>
            </div>
            <div className="ml-3">
              <p className="text-sm text-red-800 dark:text-red-200">
                <strong>Error:</strong> Failed to load analytics data. Please check your authentication and try again.
              </p>
            </div>
          </div>
        </div>
      )}

      {/* Header */}
      <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between gap-4">
        <div>
          <h1 className="text-3xl font-bold text-gray-900 dark:text-white">Analytics & Reports</h1>
          <p className="text-gray-600 dark:text-gray-400">
            Comprehensive insights into platform performance and user behavior
          </p>
        </div>

        <div className="flex items-center gap-3">
          <Select value={timeRange} onValueChange={setTimeRange}>
            <SelectTrigger className="w-40 border-gray-300 dark:border-gray-600 text-gray-900 dark:text-white bg-white dark:bg-gray-800">
              <SelectValue placeholder="Time range" />
            </SelectTrigger>
            <SelectContent className="bg-white dark:bg-gray-800 border-gray-200 dark:border-gray-700">
              <SelectItem value="7d" className="text-gray-900 dark:text-white">Last 7 days</SelectItem>
              <SelectItem value="30d" className="text-gray-900 dark:text-white">Last 30 days</SelectItem>
              <SelectItem value="90d" className="text-gray-900 dark:text-white">Last 90 days</SelectItem>
              <SelectItem value="1y" className="text-gray-900 dark:text-white">Last year</SelectItem>
            </SelectContent>
          </Select>

          <Button 
            variant="outline" 
            size="sm"
            onClick={handleRefresh}
            className="border-gray-300 dark:border-gray-600 text-gray-700 dark:text-gray-300 hover:bg-gray-50 dark:hover:bg-gray-700"
          >
            <RefreshCw className="h-4 w-4 mr-2" />
            Refresh
          </Button>

          <Button 
            variant="outline" 
            size="sm"
            onClick={handleExport}
            className="border-gray-300 dark:border-gray-600 text-gray-700 dark:text-gray-300 hover:bg-gray-50 dark:hover:bg-gray-700"
          >
            <Download className="h-4 w-4 mr-2" />
            Export
          </Button>
        </div>
      </div>

      {/* Key Metrics */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        <Card className="bg-white dark:bg-gray-800 border-gray-200 dark:border-gray-700 shadow-lg">
          <CardContent className="p-6">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm font-medium text-gray-600 dark:text-gray-400">Total Users</p>
                <p className="text-2xl font-bold text-gray-900 dark:text-white">
                  {formatNumber(displayData?.overview?.totalUsers || 0)}
                </p>
                <div className="flex items-center mt-1">
                  {getTrendIcon(12.5)}
                  <span className={`text-sm ${getTrendColor(12.5)}`}></span>
                </div>
              </div>
              <Users className="h-8 w-8 text-blue-500 dark:text-blue-400" />
            </div>
          </CardContent>
        </Card>

        <Card className="bg-white dark:bg-gray-800 border-gray-200 dark:border-gray-700 shadow-lg">
          <CardContent className="p-6">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm font-medium text-gray-600 dark:text-gray-400">Active Users</p>
                <p className="text-2xl font-bold text-gray-900 dark:text-white">
                  {formatNumber(displayData?.overview?.activeUsers || 0)}
                </p>
                <div className="flex items-center mt-1">
                  {getTrendIcon(8.2)}
                  <span className={`text-sm ${getTrendColor(8.2)}`}></span>
                </div>
              </div>
              <UserCheck className="h-8 w-8 text-green-500 dark:text-green-400" />
            </div>
          </CardContent>
        </Card>

        <Card className="bg-white dark:bg-gray-800 border-gray-200 dark:border-gray-700 shadow-lg">
          <CardContent className="p-6">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm font-medium text-gray-600 dark:text-gray-400">Revenue</p>
                <p className="text-2xl font-bold text-gray-900 dark:text-white">
                  {formatCurrency(displayData?.overview?.revenue || 0)}
                </p>
                <div className="flex items-center mt-1">
                  {getTrendIcon(15.3)}
                  <span className={`text-sm ${getTrendColor(15.3)}`}></span>
                </div>
              </div>
              <DollarSign className="h-8 w-8 text-yellow-500 dark:text-yellow-400" />
            </div>
          </CardContent>
        </Card>

        <Card className="bg-white dark:bg-gray-800 border-gray-200 dark:border-gray-700 shadow-lg">
          <CardContent className="p-6">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm font-medium text-gray-600 dark:text-gray-400">Conversion Rate</p>
                <p className="text-2xl font-bold text-gray-900 dark:text-white">
                  {formatPercentage(displayData?.overview?.conversionRate || 0)}
                </p>
                <div className="flex items-center mt-1">
                  {getTrendIcon(2.1)}
                  <span className={`text-sm ${getTrendColor(2.1)}`}></span>
                </div>
              </div>
              <Target className="h-8 w-8 text-purple-500 dark:text-purple-400" />
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Analytics Tabs */}
      <Tabs value={activeTab} onValueChange={setActiveTab} className="w-full">
        <TabsList className="grid w-full grid-cols-5 bg-white dark:bg-gray-800 border-gray-200 dark:border-gray-700">
          <TabsTrigger value="overview" className="data-[state=active]:bg-blue-600 data-[state=active]:text-white text-gray-700 dark:text-gray-300">Overview</TabsTrigger>
          <TabsTrigger value="users" className="data-[state=active]:bg-blue-600 data-[state=active]:text-white text-gray-700 dark:text-gray-300">Users</TabsTrigger>
          <TabsTrigger value="content" className="data-[state=active]:bg-blue-600 data-[state=active]:text-white text-gray-700 dark:text-gray-300">Content</TabsTrigger>
          <TabsTrigger value="revenue" className="data-[state=active]:bg-blue-600 data-[state=active]:text-white text-gray-700 dark:text-gray-300">Revenue</TabsTrigger>
        </TabsList>

        <TabsContent value="overview" className="space-y-6">
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
            <Card className="bg-white dark:bg-gray-800 border-gray-200 dark:border-gray-700 shadow-lg">
              <CardHeader>
                <CardTitle className="text-gray-900 dark:text-white">User Growth</CardTitle>
              </CardHeader>
              <CardContent>
                <div className="h-64">
                  <ResponsiveContainer width="100%" height="100%">
                    <AreaChart data={displayData?.userGrowth?.daily || []}>
                      <CartesianGrid strokeDasharray="3 3" />
                      <XAxis dataKey="date" />
                      <YAxis />
                      <Tooltip />
                      <Area type="monotone" dataKey="users" stroke="#3b82f6" fill="#3b82f6" fillOpacity={0.3} />
                    </AreaChart>
                  </ResponsiveContainer>
                </div>
              </CardContent>
            </Card>

            <Card className="bg-white dark:bg-gray-800 border-gray-200 dark:border-gray-700 shadow-lg">
              <CardHeader>
                <CardTitle className="text-gray-900 dark:text-white">Revenue Trends</CardTitle>
              </CardHeader>
              <CardContent>
                <div className="h-64">
                  <ResponsiveContainer width="100%" height="100%">
                    <LineChart data={displayData?.userGrowth?.monthly || []}>
                      <CartesianGrid strokeDasharray="3 3" />
                      <XAxis dataKey="month" />
                      <YAxis />
                      <Tooltip formatter={(value) => [formatCurrency(Number(value)), 'Revenue']} />
                      <Line type="monotone" dataKey="users" stroke="#10b981" strokeWidth={2} />
                    </LineChart>
                  </ResponsiveContainer>
                </div>
              </CardContent>
            </Card>
          </div>

          <Card className="bg-white dark:bg-gray-800 border-gray-200 dark:border-gray-700 shadow-lg">
            <CardHeader>
              <CardTitle className="text-gray-900 dark:text-white">Platform Health</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
                <div className="text-center">
                  <div className="text-3xl font-bold text-green-600 dark:text-green-400 mb-2">98.5%</div>
                  <div className="text-sm text-gray-600 dark:text-gray-400">Uptime</div>
                </div>
                <div className="text-center">
                  <div className="text-3xl font-bold text-blue-600 dark:text-blue-400 mb-2">2.3s</div>
                  <div className="text-sm text-gray-600 dark:text-gray-400">Avg Response Time</div>
                </div>
                <div className="text-center">
                  <div className="text-3xl font-bold text-purple-600 dark:text-purple-400 mb-2">99.9%</div>
                  <div className="text-sm text-gray-600 dark:text-gray-400">Success Rate</div>
                </div>
              </div>
            </CardContent>
          </Card>
        </TabsContent>

        <TabsContent value="users" className="space-y-6">
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
            <Card className="bg-white dark:bg-gray-800 border-gray-200 dark:border-gray-700 shadow-lg">
              <CardHeader>
                <CardTitle className="text-gray-900 dark:text-white">User Demographics</CardTitle>
              </CardHeader>
              <CardContent>
                <div className="h-64">
                  <ResponsiveContainer width="100%" height="100%">
                    <PieChart>
                      <Pie
                        data={[
                          { name: 'Premium Users', value: displayData?.revenue?.subscriptionBreakdown?.premium || 0, color: '#3b82f6' },
                          { name: 'Free Users', value: displayData?.revenue?.subscriptionBreakdown?.free || 0, color: '#6b7280' }
                        ]}
                        cx="50%"
                        cy="50%"
                        labelLine={false}
                        label={({ name, percent }) => `${name} ${(percent * 100).toFixed(0)}%`}
                        outerRadius={80}
                        fill="#8884d8"
                        dataKey="value"
                      >
                        {[
                          { name: 'Premium Users', value: displayData?.revenue?.subscriptionBreakdown?.premium || 0, color: '#3b82f6' },
                          { name: 'Free Users', value: displayData?.revenue?.subscriptionBreakdown?.free || 0, color: '#6b7280' }
                        ].map((entry, index) => (
                          <Cell key={`cell-${index}`} fill={entry.color} />
                        ))}
                      </Pie>
                      <Tooltip />
                    </PieChart>
                  </ResponsiveContainer>
                </div>
              </CardContent>
            </Card>

            <Card className="bg-white dark:bg-gray-800 border-gray-200 dark:border-gray-700 shadow-lg">
              <CardHeader>
                <CardTitle className="text-gray-900 dark:text-white">User Activity</CardTitle>
              </CardHeader>
              <CardContent>
                <div className="h-64">
                  <ResponsiveContainer width="100%" height="100%">
                    <BarChart data={displayData?.userGrowth?.weekly || []}>
                      <CartesianGrid strokeDasharray="3 3" />
                      <XAxis dataKey="week" />
                      <YAxis />
                      <Tooltip />
                      <Bar dataKey="users" fill="#10b981" />
                    </BarChart>
                  </ResponsiveContainer>
                </div>
              </CardContent>
            </Card>
          </div>

          <Card className="bg-white dark:bg-gray-800 border-gray-200 dark:border-gray-700 shadow-lg">
            <CardHeader>
              <CardTitle className="text-gray-900 dark:text-white">User Statistics</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="grid grid-cols-1 md:grid-cols-4 gap-6">
                <div className="text-center">
                  <div className="text-2xl font-bold text-blue-600 dark:text-blue-400 mb-2">
                    {formatNumber(displayData?.overview?.totalUsers || 0)}
                  </div>
                  <div className="text-sm text-gray-600 dark:text-gray-400">Total Users</div>
                </div>
                <div className="text-center">
                  <div className="text-2xl font-bold text-green-600 dark:text-green-400 mb-2">
                    {formatNumber(displayData?.overview?.activeUsers || 0)}
                  </div>
                  <div className="text-sm text-gray-600 dark:text-gray-400">Active Users</div>
                </div>
                <div className="text-center">
                  <div className="text-2xl font-bold text-purple-600 dark:text-purple-400 mb-2">
                    {formatNumber(displayData?.overview?.newUsers || 0)}
                  </div>
                  <div className="text-sm text-gray-600 dark:text-gray-400">New Users (7d)</div>
                </div>
                <div className="text-center">
                  <div className="text-2xl font-bold text-yellow-600 dark:text-yellow-400 mb-2">
                    {formatTime(displayData?.overview?.averageSessionTime || 0)}
                  </div>
                  <div className="text-sm text-gray-600 dark:text-gray-400">Avg Session Time</div>
                </div>
              </div>
            </CardContent>
          </Card>
        </TabsContent>

        <TabsContent value="content" className="space-y-6">
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
            <Card className="bg-white dark:bg-gray-800 border-gray-200 dark:border-gray-700 shadow-lg">
              <CardHeader>
                <CardTitle className="text-gray-900 dark:text-white">Top Performing Videos</CardTitle>
              </CardHeader>
              <CardContent>
                <div className="space-y-4">
                  {displayData?.contentPerformance?.videos?.slice(0, 5).map((video, index) => (
                    <div key={index} className="flex items-center justify-between">
                      <span className="text-sm text-gray-700 dark:text-gray-300 truncate">{video.title}</span>
                      <div className="flex items-center gap-2">
                        <Badge variant="outline" className="border-gray-300 dark:border-gray-600 text-gray-700 dark:text-gray-300">
                          {formatNumber(video.views)} views
                        </Badge>
                        <Badge variant="secondary" className="bg-gray-100 dark:bg-gray-700 text-gray-700 dark:text-gray-300">
                          {video.completionRate}% completion
                        </Badge>
                      </div>
                    </div>
                  ))}
                </div>
              </CardContent>
            </Card>

            <Card className="bg-white dark:bg-gray-800 border-gray-200 dark:border-gray-700 shadow-lg">
              <CardHeader>
                <CardTitle className="text-gray-900 dark:text-white">Exercise Performance</CardTitle>
              </CardHeader>
              <CardContent>
                <div className="space-y-4">
                  {displayData?.contentPerformance?.exercises?.slice(0, 5).map((exercise, index) => (
                    <div key={index} className="flex items-center justify-between">
                      <span className="text-sm text-gray-700 dark:text-gray-300 truncate">{exercise.title}</span>
                      <div className="flex items-center gap-2">
                        <Badge variant="outline" className="border-gray-300 dark:border-gray-600 text-gray-700 dark:text-gray-300">
                          {formatNumber(exercise.attempts)} attempts
                        </Badge>
                        <Badge variant="secondary" className="bg-gray-100 dark:bg-gray-700 text-gray-700 dark:text-gray-300">
                          {exercise.successRate}% success
                        </Badge>
                      </div>
                    </div>
                  ))}
                </div>
              </CardContent>
            </Card>
          </div>

          <Card className="bg-white dark:bg-gray-800 border-gray-200 dark:border-gray-700 shadow-lg">
            <CardHeader>
              <CardTitle className="text-gray-900 dark:text-white">Test Performance</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="h-64">
                <ResponsiveContainer width="100%" height="100%">
                  <BarChart data={displayData?.contentPerformance?.tests || []}>
                    <CartesianGrid strokeDasharray="3 3" />
                    <XAxis dataKey="title" angle={-45} textAnchor="end" height={100} />
                    <YAxis />
                    <Tooltip formatter={(value, name) => [value, name === 'completions' ? 'Completions' : 'Average Score']} />
                    <Bar dataKey="completions" fill="#3b82f6" name="completions" />
                    <Bar dataKey="averageScore" fill="#10b981" name="averageScore" />
                  </BarChart>
                </ResponsiveContainer>
              </div>
            </CardContent>
          </Card>
        </TabsContent>

        <TabsContent value="revenue" className="space-y-6">
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
            <Card className="bg-white dark:bg-gray-800 border-gray-200 dark:border-gray-700 shadow-lg">
              <CardHeader>
                <CardTitle className="text-gray-900 dark:text-white">Revenue Breakdown</CardTitle>
              </CardHeader>
              <CardContent>
                <div className="space-y-4">
                  <div className="flex items-center justify-between">
                    <span className="text-gray-700 dark:text-gray-300">Monthly Revenue</span>
                    <span className="font-semibold text-gray-900 dark:text-white">{formatCurrency(displayData?.revenue?.monthly || 0)}</span>
                  </div>
                  <div className="flex items-center justify-between">
