            />
          </div>
        </div>

        {/* Quick Navigation */}
        {selectedLocation && (
          <div className="flex items-center gap-2">
            <Button
              size="sm"
              variant="outline"
              onClick={() => {
                const currentIndex = filteredLocations.findIndex(l => l.id === selectedLocation.id);
