];

// Create 24 questions for a test
function createTestQuestions(testIndex) {
  const testQuestions = [];
  const questionsPerCategory = 6; // 6 questions from each of 4 categories

  const categories = ['British History', 'Government & Politics', 'Geography & Culture', 'Laws & Society'];

  for (const category of categories) {
    const categoryQuestions = sampleQuestions.filter(q => q.category === category);
    for (let i = 0; i < questionsPerCategory; i++) {
      const questionIndex = (testIndex + i) % categoryQuestions.length;
      const question = categoryQuestions[questionIndex];
      testQuestions.push({
        id: testQuestions.length + 1,
        question: question.question,
        options: question.options,
        correctAnswer: question.correctAnswer,
        explanation: question.explanation,
        category: question.category
      });
    }
  }

  return testQuestions;
}

console.log('📝 Sample questions created:');
console.log(`Total questions available: ${sampleQuestions.length}`);
console.log(`Questions per category:`);
const categories = ['British History', 'Government & Politics', 'Geography & Culture', 'Laws & Society'];
categories.forEach(category => {
  const count = sampleQuestions.filter(q => q.category === category).length;
  console.log(`  - ${category}: ${count} questions`);
});

console.log('\n📋 Sample test questions (first 5):');
const sampleTestQuestions = createTestQuestions(1);
sampleTestQuestions.slice(0, 5).forEach((q, i) => {
  console.log(`${i + 1}. ${q.question}`);
  console.log(`   Options: ${q.options.join(', ')}`);
  console.log(`   Correct: ${q.options[q.correctAnswer]}`);
  console.log(`   Category: ${q.category}`);
  console.log('');
});

console.log('✅ Script completed. Use this data to update your practice tests in the database.');

