    });

    const content = JSON.parse(response.choices[0].message.content || '{}');
    console.log(`Generated ${content.tests?.length || 0} tests, ${content.timelineEvents?.length || 0} events, ${content.games?.length || 0} games`);
    return content;
  } catch (error) {
